paths:
  tmp0:
    post:
      description: Add an Observable/IOC to a Case
      parameters:
      - description: The ID of the case
        example: ''
        in: query
        name: Case ID
        required: true
        schema: {type: string}
      - description: Data of the observable, like the IP or Hash
        example: ''
        in: query
        name: Data
        required: true
        schema: {type: string}
      - description: Description of the Observable/IOC
        example: ''
        in: query
        name: Description
        required: true
        schema: {type: string}
      - description: Traffic light protocol value, 0 - white, 1 - green, 2 - amber,
          3 - red
        example: ''
        in: body
        name: TLP Value
        required: true
        schema: {type: string}
      - description: Is this Observable an Indicator of Compromise?
        example: ''
        in: query
        name: Is IOC
        required: true
        schema: {type: boolean}
      - description: Type of data the Observable is. Must be one of autonomous-system,
          domain, file, filename, fqdn, hash, ip, mail, mail_subject, other, regexp,
          registry, uri_path, url, or user-agent
        example: ''
        in: query
        name: Data Type
        required: true
        schema: {type: string}
      - description: Comma seperated tag values to be applied to this Observable
        example: ''
        in: query
        name: Tags
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Add Observable/IOC to Case
  tmp1:
    post:
      description: Create a case in the Hive
      parameters:
      - description: Title of the case
        example: Example Case
        in: query
        name: Title
        required: true
        schema: {type: string}
      - description: Description of the case
        example: Description of example case
        in: query
        name: Description
        required: true
        schema: {type: string}
      - description: Traffic Light Protocol value of this case
        example: '2'
        in: body
        name: TLP
        required: false
        schema: {type: string}
      - description: Severity of the case
        example: '2'
        in: body
        name: Severity
        required: false
        schema: {type: string}
      - description: Flag of the case
        example: ''
        in: query
        name: Flag
        required: false
        schema: {type: boolean}
      - description: List of tags to add to the case. CSV.
        example: tag1,tag2
        in: query
        name: Tags
        required: false
        schema: {type: string}
      - description: Date the case started
        example: ''
        in: query
        name: Start Date
        required: false
        schema: {type: string}
      - description: List of custom fields. JSON object.
        example: ''
        in: query
        name: Custom Fields
        required: false
        schema: {type: string}
      - description: Template for the case
        example: ''
        in: query
        name: Template
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: The Hive Create Case
  tmp2:
    post:
      description: Get details about a case in the Hive
      parameters:
      - description: The ID of the case
        example: ''
        in: query
        name: Case ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Get Case Details
  tmp3:
    post:
      description: Get observables that belong to a case in the Hive
      parameters:
      - description: The ID of the case
        example: ''
        in: query
        name: Case ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Get Case Observables Details
  tmp4:
