    post:
      description: Nuix Advanced Process Case Evidence
      parameters:
      - description: Case Evidence JSON Parameter. See example at http://<YOUR_NUIX_INSTANCE>:8080/nuix-restful-service/svc/swagger-ui.html#!/Processing/bulkIngestionIntoSimpleCaseUsingPOST
        example: ''
        in: query
        name: Process Case Evidence JSON
        required: true
        schema: {type: string}
      - description: Nuix License Shortname, this will override the licence set in
          the asset.
        example: ''
        in: query
        name: Licence Shortname
        required: false
        schema: {type: string}
      - description: Provide an authorization token to be used with this integration.
        example: ''
        in: query
        name: Optional Authorization Token
        required: false
        schema: {type: string}
      - description: Defaults to True. If Logout is false, the Authorization Token
          will be returned
        example: ''
        in: query
        name: Logout
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Nuix Advanced Process Case Evidence
  tmp1:
