      - description: Apply metadata to family items
        example: ''
        in: query
        name: Apply To Family
        required: false
        schema: {type: boolean}
      - description: Apply metadata to duplicate items
        example: ''
        in: query
        name: Apply To Duplicates
        required: false
        schema: {type: boolean}
