      - description: Provide an authorization token to be used with this integration.
        example: ''
        in: query
        name: Optional Authorization Token
        required: false
        schema: {type: string}
      - description: Defaults to True. If Logout is false, the Authorization Token
          will be returned.
        example: ''
        in: query
        name: Logout
        required: false
        schema: {type: boolean}
