            "schema": { "type": "boolean", "default": false }
          },
          {
            "name": "card_list",
            "in": "query",
            "description": "Whether to include the parent list with card results",
            "required": false,
            "schema": { "type": "boolean", "default": false }
          },
          {
            "name": "card_members",
            "in": "query",
