{
  "__comment": "THIS FILE WAS AUTO-GENERATED USING https://github.com/revoltchat/api. DO NOT EDIT.",
  "openapi": "3.0.0",
  "info": {
    "title": "Revolt API",
    "version": "0.5.3-alpha.12",
    "description": "User-first privacy focused chat platform built with modern web technologies.\n\n# Authentication\n\nCurrently all relevant requests are authenticated by the use of two headers which identify a session.\n\n<SecurityDefinitions />",
    "termsOfService": "https://revolt.chat/terms",
    "contact": {
      "name": "API Support",
      "email": "contact@revolt.chat",
      "url": "https://revolt.chat"
    },
    "x-logo": {
      "url": "https://revolt.chat/header.png",
      "altText": "Revolt Header"
    }
  },
  "tags": [
    {
      "name": "Core",
      "description": "Use in your applications to determine information about the Revolt node."
    },
    {
      "name": "Onboarding",
      "description": "After signing up to Revolt, users must pick a unique username."
    },
    {
      "name": "Account",
      "description": "Manage your account."
    },
    {
      "name": "Session",
      "description": "Create and manage sessions."
    },
    {
      "name": "User Information",
      "description": "Query and fetch users on Revolt"
    },
    {
      "name": "Direct Messaging",
      "description": "Direct message other users on Revolt"
    },
    {
      "name": "Relationships",
      "description": "Manage your friendships and block list on the platform"
    },
    {
      "name": "Channel Information",
      "description": "Query and fetch channels on Revolt"
    },
    {
      "name": "Channel Invites",
      "description": "Create and manage invites for channels"
    },
    {
      "name": "Channel Permissions",
      "description": "Manage permissions for channels"
    },
    {
      "name": "Messaging",
      "description": "Send and manipulate messages"
    },
    {
      "name": "Groups",
      "description": "Create, invite users and manipulate groups"
    },
    {
      "name": "Voice",
      "description": "Join and talk with other users"
    },
    {
      "name": "Server Information",
      "description": "Query and fetch servers on Revolt"
    },
    {
      "name": "Server Members",
      "description": "Find and edit server members"
    },
    {
      "name": "Server Permissions",
      "description": "Manage permissions for servers"
    },
    {
      "name": "Bots",
      "description": "Create and edit bots."
    },
    {
      "name": "Invites",
      "description": "View, join and delete invites"
    },
    {
      "name": "Sync",
      "description": "Upload and retrieve any JSON data between clients"
    },
    {
      "name": "Web Push",
      "description": "Subscribe to and receive Revolt push notifications while offline"
    }
  ],
  "x-tagGroups": [
    {
      "name": "Chat Platform",
      "tags": [
        "Core",
        "Onboarding"
      ]
    },
    {
      "name": "Auth",
      "tags": [
        "Account",
        "Session"
      ]
    },
    {
      "name": "Users",
      "tags": [
        "User Information",
        "Direct Messaging",
        "Relationships"
      ]
    },
    {
      "name": "Channels",
      "tags": [
        "Channel Information",
        "Channel Invites",
        "Channel Permissions",
        "Messaging",
        "Groups",
        "Voice"
      ]
    },
    {
      "name": "Servers",
      "tags": [
        "Server Information",
        "Server Members",
        "Server Permissions"
      ]
    },
    {
      "name": "Bots",
      "tags": [
        "Bots"
      ]
    },
    {
      "name": "Miscellaneous",
      "tags": [
        "Invites",
        "Sync",
        "Web Push"
      ]
    }
  ],
  "paths": {
    "/": {
      "get": {
        "summary": "Query Node",
        "description": "This returns information about which features are enabled on the remote node.",
        "tags": [
          "Core"
        ],
        "responses": {
          "200": {
            "description": "Server node information.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/RevoltConfiguration"
                }
              }
            }
          }
        }
      }
    },
    "/onboard/hello": {
      "get": {
        "summary": "Check Onboarding Status",
        "description": "This will tell you whether the current account requires onboarding or whether you can continue to send requests as usual. You may skip calling this if you're restoring an existing session.",
        "tags": [
          "Onboarding"
        ],
        "responses": {
          "200": {
            "description": "Onboarding information.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/OnboardingInformation"
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/onboard/complete": {
      "post": {
        "summary": "Complete Onboarding",
        "description": "This sets a new username, completes onboarding and allows a user to start using Revolt.",
        "tags": [
          "Onboarding"
        ],
        "requestBody": {
          "description": "Data about wanted user information.",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "username": {
                    "description": "An alphanumeric username which is used to identify the user on the platform.",
                    "minLength": 2,
                    "maxLength": 32,
                    "pattern": "^[a-zA-Z0-9-_]+$",
                    "type": "string"
                  }
                },
                "required": [
                  "username"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Successfully set username"
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/account": {
      "get": {
        "summary": "Fetch Account",
        "description": "Fetch account information.",
        "tags": [
          "Account"
        ],
        "responses": {
          "200": {
            "description": "Account Information",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/Account"
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/account/create": {
      "post": {
        "summary": "Create Account",
        "description": "Create a new account.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Create Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "email": {
                    "description": "Valid email address",
                    "type": "string"
                  },
                  "password": {
                    "description": "Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  },
                  "invite": {
                    "description": "Invite Code",
                    "type": "string"
                  },
                  "captcha": {
                    "description": "Captcha verification code",
                    "type": "string"
                  }
                },
                "required": [
                  "email",
                  "password"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Created account."
          }
        }
      }
    },
    "/auth/account/reverify": {
      "post": {
        "summary": "Resend Verification",
        "description": "Resend account creation verification email.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Resend Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "email": {
                    "description": "Email",
                    "type": "string"
                  },
                  "captcha": {
                    "description": "Captcha verification token",
                    "type": "string"
                  }
                },
                "required": [
                  "email"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Resent verification."
          }
        }
      }
    },
    "/auth/account/verify/:code": {
      "post": {
        "summary": "Verify Email",
        "description": "Verifies an email with code.",
        "tags": [
          "Account"
        ],
        "parameters": [
          {
            "name": "code",
            "in": "path",
            "required": true,
            "description": "Verification Code",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Verified email."
          }
        }
      }
    },
    "/auth/account/reset_password": {
      "post": {
        "summary": "Send Password Reset",
        "description": "Send password reset email.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Reset Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "email": {
                    "description": "Email",
                    "type": "string"
                  },
                  "captcha": {
                    "description": "Captcha verification token",
                    "type": "string"
                  }
                },
                "required": [
                  "email"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Sent password reset."
          }
        }
      },
      "patch": {
        "summary": "Password Reset",
        "description": "Confirm password reset.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Reset Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "password": {
                    "description": "Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  },
                  "token": {
                    "description": "Password reset token",
                    "type": "string"
                  }
                },
                "required": [
                  "password",
                  "token"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Password was changed."
          }
        }
      }
    },
    "/auth/account/change/password": {
      "patch": {
        "summary": "Change Password",
        "description": "Change account password.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Change Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "password": {
                    "description": "Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  },
                  "current_password": {
                    "description": "Current Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  }
                },
                "required": [
                  "current_password",
                  "password"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Password changed."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/account/change/email": {
      "patch": {
        "summary": "Change Email",
        "description": "Change account email.",
        "tags": [
          "Account"
        ],
        "requestBody": {
          "description": "Change Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "current_password": {
                    "description": "Current Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  },
                  "email": {
                    "description": "Valid email",
                    "type": "string"
                  }
                },
                "required": [
                  "current_password",
                  "email"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Email changed."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/session/login": {
      "post": {
        "summary": "Login",
        "description": "Login to an account.",
        "tags": [
          "Session"
        ],
        "requestBody": {
          "description": "Login Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "email": {
                    "description": "Valid email address",
                    "type": "string"
                  },
                  "password": {
                    "description": "Password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  },
                  "challenge": {
                    "description": "Security Key Challenge",
                    "type": "string"
                  },
                  "friendly_name": {
                    "description": "Session Friendly Name",
                    "minLength": 0,
                    "maxLength": 72,
                    "type": "string"
                  },
                  "captcha": {
                    "description": "Captcha verification code",
                    "type": "string"
                  }
                },
                "required": [
                  "email"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Logged in.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/Session"
                }
              }
            }
          }
        }
      }
    },
    "/auth/session/logout": {
      "post": {
        "summary": "Logout",
        "description": "Close current session.",
        "tags": [
          "Session"
        ],
        "responses": {
          "204": {
            "description": "Logged out."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/session/:session": {
      "patch": {
        "summary": "Edit Session",
        "description": "Edit session information.",
        "tags": [
          "Session"
        ],
        "parameters": [
          {
            "name": "session",
            "in": "path",
            "required": true,
            "description": "Session ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "requestBody": {
          "description": "Edit Data",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "friendly_name": {
                    "description": "Session Friendly Name",
                    "minLength": 0,
                    "maxLength": 72,
                    "type": "string"
                  }
                },
                "required": [
                  "friendly_name"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Edited session."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      },
      "delete": {
        "summary": "Delete Session",
        "description": "Delete a specific session.",
        "tags": [
          "Session"
        ],
        "parameters": [
          {
            "name": "session",
            "in": "path",
            "required": true,
            "description": "Session ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Deleted session."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/auth/session/all": {
      "get": {
        "summary": "Fetch Sessions",
        "description": "Fetch all sessions.",
        "tags": [
          "Session"
        ],
        "responses": {
          "200": {
            "description": "Sessions",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/definitions/SessionInfo"
                  },
                  "definitions": {
                    "SessionInfo": {
                      "type": "object",
                      "properties": {
                        "_id": {
                          "description": "Session ID",
                          "pattern": "[0123456789ABCDEFGHJKMNPQRSTVWXYZ]{26}",
                          "type": "string"
                        },
                        "name": {
                          "description": "Device name",
                          "type": "string"
                        }
                      },
                      "required": [
                        "_id",
                        "name"
                      ]
                    }
                  }
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      },
      "delete": {
        "summary": "Delete All Sessions",
        "description": "Delete all active sesssions.",
        "tags": [
          "Session"
        ],
        "parameters": [
          {
            "name": "revoke_self",
            "in": "query",
            "description": "Whether to revoke current session too.",
            "schema": {
              "type": "boolean"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Deleted session."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/users/:user": {
      "get": {
        "summary": "Fetch User",
        "description": "Retrieve a user's information.",
        "tags": [
          "User Information"
        ],
        "parameters": [
          {
            "name": "user",
            "in": "path",
            "required": true,
            "description": "User ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "User information.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/User"
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          },
          {
            "Bot Token": []
          }
        ]
      }
    },
    "/users/@me": {
      "patch": {
        "summary": "Edit User",
        "description": "Edit your user object.",
        "tags": [
          "User Information"
        ],
        "requestBody": {
          "description": "Requested changes to user object.",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "status": {
                    "description": "User status",
                    "type": "object",
                    "properties": {
                      "text": {
                        "description": "Custom status text",
                        "minLength": 1,
                        "maxLength": 128,
                        "type": "string"
                      },
                      "presence": {
                        "$ref": "#/definitions/Presence"
                      }
                    }
                  },
                  "profile": {
                    "description": "User profile data",
                    "type": "object",
                    "properties": {
                      "content": {
                        "description": "Text to set as user profile description",
                        "maxLength": 2000,
                        "type": "string"
                      },
                      "background": {
                        "description": "Autumn file ID, [learn more](https://example.com/TODO).",
                        "minLength": 1,
                        "maxLength": 128,
                        "type": "string"
                      }
                    }
                  },
                  "avatar": {
                    "description": "Autumn file ID, [learn more](https://example.com/TODO).",
                    "minLength": 1,
                    "maxLength": 128,
                    "type": "string"
                  },
                  "remove": {
                    "description": "Field to remove from user object",
                    "enum": [
                      "Avatar",
                      "ProfileBackground",
                      "ProfileContent",
                      "StatusText"
                    ],
                    "type": "string"
                  }
                },
                "definitions": {
                  "Presence": {
                    "description": "User presence",
                    "enum": [
                      "Busy",
                      "Idle",
                      "Invisible",
                      "Online"
                    ],
                    "type": "string"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Succesfully changed user object."
          }
        },
        "security": [
          {
            "Session Token": []
          },
          {
            "Bot Token": []
          }
        ]
      },
      "get": {
        "summary": "Fetch Self",
        "description": "Retrieve your user information.",
        "tags": [
          "User Information"
        ],
        "parameters": [
          {
            "name": "user",
            "in": "path",
            "required": true,
            "description": "User ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "User information.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/User"
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          },
          {
            "Bot Token": []
          }
        ]
      }
    },
    "/users/@me/username": {
      "patch": {
        "summary": "Change Username",
        "description": "Change your username.",
        "tags": [
          "User Information"
        ],
        "requestBody": {
          "description": "Requested change to username.",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "username": {
                    "description": "New username",
                    "minLength": 2,
                    "maxLength": 32,
                    "pattern": "^[a-zA-Z0-9_.]+$",
                    "type": "string"
                  },
                  "password": {
                    "description": "Current account password",
                    "minLength": 8,
                    "maxLength": 1024,
                    "type": "string"
                  }
                },
                "required": [
                  "password",
                  "username"
                ]
              }
            }
          }
        },
        "responses": {
          "204": {
            "description": "Succesfully changed user object."
          }
        },
        "security": [
          {
            "Session Token": []
          }
        ]
      }
    },
    "/users/:user/profile": {
      "get": {
        "summary": "Fetch User Profile",
        "description": "Retrieve a user's profile data.",
        "tags": [
          "User Information"
        ],
        "parameters": [
          {
            "name": "user",
            "in": "path",
            "required": true,
            "description": "User ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "User profile.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/definitions/Profile"
                }
              }
            }
          }
        },
        "security": [
          {
            "Session Token": []
          },
          {
            "Bot Token": []
          }
        ]
      }
    },
    "/users/:user/default_avatar": {
      "get": {
        "summary": "Fetch Default Avatar",
        "description": "This returns a default avatar based on the given id.",
        "tags": [
          "User Information"
        ],
        "parameters": [
          {
            "name": "user",
            "in": "path",
            "required": true,
            "description": "User ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Default avatar in PNG format",
            "content": {
              "image/png": {
                "schema": {
                  "type": "string",
                  "format": "binary"
                }
              }
            }
          }
        }
      }
    },
    "/users/:user/mutual": {
      "get": {
        "summary": "Fetch Mutual Friends And Servers",
        "description": "Retrieve a list of mutual friends and servers with another user.",
        "tags": [
          "User Information"
        ],
        "parameters": [
          {
            "name": "user",
            "in": "path",
            "required": true,
            "description": "User ID",
            "schema": {
              "$ref": "#/definitions/Id"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Mutual friends and servers.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "users": {
                      "description": "Array of user IDs who both you and the other user are friends with.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    },
                    "servers": {
                      "description": "Array of server IDs who both you and the other user are members of.",
                      "type": "array",
                      "items": {
                        "type": "string"
