{
	"openapi": "3.0.0",
	"info": {
		"title": "Nadybot API",
		"description": "This API provides access to Nadybot functions in a REST API",
		"license": {
			"name": "GPL3",
			"url": "https://www.gnu.org/licenses/gpl-3.0.en.html"
		},
		"version": "6.0.5"
	},
	"servers": [
		{
			"url": "/api"
		}
	],
	"components": {
		"schemas": {
			"SettingOption": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "Name of this option for displaying"
					},
					"value": {
						"oneOf": [
							{
								"type": "string"
							},
							{
								"type": "integer"
							}
						],
						"description": "Which value does this option represent?"
					}
				}
			},
			"ModuleAccessLevel": {
				"allOf": [
					{
						"$ref": "#/components/schemas/SettingOption"
					},
					{
						"type": "object",
						"properties": {
							"numeric_value": {
								"type": "integer",
								"description": "Higher value means fewer rights. Use this to sort on"
							},
							"enabled": {
								"type": "boolean",
								"description": "Some ranks only work if a module is enabled"
							}
						}
					}
				]
			},
			"Audit": {
				"type": "object",
				"properties": {
					"id": {
						"type": "integer",
						"description": "ID of this audit entry"
					},
					"actor": {
						"type": "string",
						"description": "The person doing something"
					},
					"actee": {
						"type": "string",
						"nullable": true,
						"description": "The person the actor is interacting with. Not set if not applicable"
					},
					"action": {
						"type": "string",
						"description": "What did the actor do"
					},
					"value": {
						"type": "string",
						"nullable": true,
						"description": "Optional value for the action"
					},
					"time": {
						"type": "integer",
						"description": "time when it happened"
					}
				}
			},
			"CmdSourceMapping": {
				"type": "object",
				"properties": {
					"sub_source": {
						"type": "string",
						"nullable": true,
						"description": "The value for the sub-source, or null if none"
					},
					"permission_set": {
						"type": "string",
						"description": "The permission set to map $source to"
					},
					"cmd_prefix": {
						"type": "string",
						"description": "The prefix that triggers a command if it's the first letter"
					},
					"cmd_prefix_optional": {
						"type": "boolean",
						"description": "Is the prefix required to interpret the msg as command or optional"
					},
					"unknown_cmd_feedback": {
						"type": "boolean",
						"description": "Shall we report an error if the command doesn't exist"
					}
				}
			},
			"CmdSource": {
				"type": "object",
				"properties": {
					"source": {
						"type": "string",
						"description": "The name/mask of this command source If the name contains an asterisk wildcard, it represents a mask of possible sources, e.g. aotell(*)"
					},
					"has_sub_sources": {
						"type": "boolean"
					},
					"mappings": {
						"type": "array",
						"description": "A list of permission set mappings this command source maps to",
						"items": {
							"$ref": "#/components/schemas/CmdSourceMapping"
						}
					}
				}
			},
			"RouteHopColor": {
				"type": "object",
				"properties": {
					"hop": {
						"type": "string",
						"description": "The hop mask (discord, *, aopriv, ...)"
					},
					"where": {
						"type": "string",
						"nullable": true,
						"description": "The channel for which to apply these colors or null for all"
					},
					"via": {
						"type": "string",
						"nullable": true,
						"description": "Only apply this color if the event was routed via this hop"
					},
					"tag_color": {
						"type": "string",
						"nullable": true,
						"description": "The 6 hex digits of the tag color, like FFFFFF"
					},
					"text_color": {
						"type": "string",
						"nullable": true,
						"description": "The 6 hex digits of the text color, like FFFFFF"
					}
				}
			},
			"RouteHopFormat": {
				"type": "object",
				"properties": {
					"hop": {
						"type": "string",
						"description": "The hop mask (discord, *, aopriv, ...)"
					},
					"where": {
						"type": "string",
						"nullable": true,
						"description": "The channel for which to apply these, or null for all"
					},
					"via": {
						"type": "string",
						"nullable": true,
						"description": "Only apply these settings if the event was routed via this hop"
					},
					"render": {
						"type": "boolean",
						"description": "Whether to render this tag or not"
					},
					"format": {
						"type": "string",
						"description": "The format what the text of the tag should look like"
					}
				}
			},
			"ConfigModule": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "Name of the module"
					},
					"num_commands_enabled": {
						"type": "integer",
						"description": "How many commands are enabled"
					},
					"num_commands_disabled": {
						"type": "integer",
						"description": "How many commands are disabled"
					},
					"num_events_enabled": {
						"type": "integer",
						"description": "How many events are enabled"
					},
					"num_events_disabled": {
						"type": "integer",
						"description": "How many events are disabled"
					},
					"num_settings": {
						"type": "integer",
						"description": "How many settings are there?"
					},
					"description": {
						"type": "string",
						"nullable": true,
						"description": "Description of the module or null if none"
					}
				}
			},
			"Operation": {
				"type": "object",
				"properties": {
					"op": {
						"type": "string",
						"description": "The operation to perform"
					}
				}
			},
			"CmdPermission": {
				"type": "object",
				"properties": {
					"permission_set": {
						"type": "string",
						"description": "The name of the permission-set"
					},
					"access_level": {
						"type": "string",
						"description": "The access-level (member,admin,guest,all,etc)"
					},
					"enabled": {
						"type": "boolean",
						"description": "Is the (sub-)command enabled on this permission-set"
					}
				}
			},
			"CmdCfg": {
				"type": "object",
				"properties": {
					"cmd": {
						"type": "string"
					},
					"description": {
						"type": "string"
					},
					"permissions": {
						"type": "array",
						"items": {
							"$ref": "#/components/schemas/CmdPermission"
						}
					}
				}
			},
			"ModuleSubcommand": {
				"allOf": [
					{
						"$ref": "#/components/schemas/CmdCfg"
					},
					{
						"type": "object",
						"properties": []
					}
				]
			},
			"ModuleCommand": {
				"allOf": [
					{
						"$ref": "#/components/schemas/ModuleSubcommand"
					},
					{
						"type": "object",
						"properties": {
							"subcommands": {
								"type": "array",
								"description": "A list of subcommands for this command. Subcommands can have different rights, but cannot be enabled without the command itself being enabled. *",
								"items": {
									"$ref": "#/components/schemas/ModuleSubcommand"
								}
							}
						}
					}
				]
			},
			"ModuleSubcommandChannel": {
				"type": "object",
				"properties": {
					"access_level": {
						"type": "string",
						"description": "The access level you need to have in order to be allowed to use this command in this channel"
					},
					"enabled": {
						"type": "boolean",
						"description": "Can this command be used in this channel?"
					}
				}
			},
			"ModuleEventConfig": {
				"type": "object",
				"properties": {
					"event": {
						"type": "string",
						"description": "The event for this module"
					},
					"handler": {
						"type": "string",
						"description": "The function handling this event"
					},
					"description": {
						"type": "string",
						"description": "What is supposed to happed when this event occurs?"
					},
					"enabled": {
						"type": "boolean",
						"description": "Is the event handler turned on?"
					}
				}
			},
			"ModuleSetting": {
				"type": "object",
				"properties": {
					"type": {
						"type": "string",
						"description": "The type of this setting (bool, number, options, etc)"
					},
					"name": {
						"type": "string",
						"description": "The name of the setting"
					},
					"value": {
						"oneOf": [
							{
								"type": "string"
							},
							{
								"type": "integer"
							},
							{
								"type": "boolean"
							},
							{
								"type": "null"
							}
						],
						"nullable": true,
						"description": "The current value"
					},
					"options": {
						"type": "array",
						"description": "A list of predefined options to pick from",
						"items": {
							"$ref": "#/components/schemas/SettingOption"
						}
					},
					"editable": {
						"type": "boolean",
						"description": "Is this a fixed setting (like database version) or can it be changed?"
					},
					"description": {
						"type": "string",
						"description": "A description of what this setting is for"
					},
					"help": {
						"type": "string",
						"nullable": true
					}
				}
			},
			"NewNews": {
				"type": "object",
				"properties": {
					"time": {
						"type": "integer",
						"description": "Unix timestamp when this was created"
					},
					"name": {
						"type": "string",
						"description": "Name of the character who created the entry"
					},
					"news": {
						"type": "string",
						"description": "Text of these news"
					},
					"sticky": {
						"type": "boolean",
						"description": "Set to true if this is pinned above all unpinned news"
					},
					"deleted": {
						"type": "boolean",
						"description": "Set to true if this is actually deleted"
					}
				}
			},
			"News": {
				"allOf": [
					{
						"$ref": "#/components/schemas/NewNews"
					},
					{
						"type": "object",
						"properties": {
							"id": {
								"type": "integer",
								"description": "The internal ID of this news entry"
							},
							"uuid": {
								"type": "string",
								"description": "The UUID of this news entry"
							}
						}
					}
				]
			},
			"Player": {
				"type": "object",
				"properties": {
					"charid": {
						"type": "integer",
						"description": "The character ID as used by Anarchy Online"
					},
					"first_name": {
						"type": "string",
						"description": "The character's first name (the name before $name)"
					},
					"name": {
						"type": "string",
						"description": "The character's name as it appears in the game"
					},
					"last_name": {
						"type": "string",
						"description": "The character's last name (the name after $name)"
					},
					"level": {
						"type": "integer",
						"nullable": true,
						"description": "What level (1-220) is the character or null if unknown"
					},
					"breed": {
						"type": "string",
						"description": "Any of Nano, Solitus, Atrox or Opifex. Also empty string if unknown"
					},
					"gender": {
						"type": "string",
						"description": "Male, Female, Neuter or an empty string if unknown"
					},
					"faction": {
						"type": "string",
						"description": "Omni, Clan, Neutral or an empty string if unknown"
					},
					"profession": {
						"type": "string",
						"nullable": true,
						"description": "The long profession name (e.g. \"Enforcer\", not \"enf\" or \"enfo\") or an empty string if unknown"
					},
					"prof_title": {
						"type": "string",
						"description": "The title-level title for the profession of this player For example \"The man\", \"Don\" or empty if unknown."
					},
					"ai_rank": {
						"type": "string",
						"description": "The name of the ai_level as a rank or empty string if unknown"
					},
					"ai_level": {
						"type": "integer",
						"nullable": true,
						"description": "AI level of this player or null if unknown"
					},
					"org_id": {
						"type": "integer",
						"nullable": true,
						"description": "The id of the org this player is in or null if none or unknown"
					},
					"org": {
						"type": "string",
						"nullable": true,
						"description": "The name of the org this player is in or null if none/unknown"
					},
					"org_rank": {
						"type": "string",
						"nullable": true,
						"description": "The name of the rank the player has in their org (Veteran, Apprentice) or null if not in an org or unknown"
					},
					"org_rank_id": {
						"type": "integer",
						"nullable": true,
						"description": "The numeric rank of the player in their org or null if not in an org/unknown"
					},
					"dimension": {
						"type": "integer",
						"nullable": true,
						"description": "In which dimension (RK server) is this character? 4 for test, 5 for RK5, 6 for RK19"
					},
					"head_id": {
						"type": "integer",
						"nullable": true,
						"description": "Which head is the player using"
					},
					"pvp_rating": {
						"type": "integer",
						"nullable": true,
						"description": "Numeric PvP-rating of the player (1-7) or null if unknown"
					},
					"pvp_title": {
						"type": "string",
						"nullable": true,
						"description": "Name of the player's PvP title derived from their $pvp_rating or null if unknown"
					},
					"last_update": {
						"type": "integer",
						"nullable": true,
						"description": "Unix timestamp of the last update of these data"
					}
				},
				"description": "This represents the data the bot stores about a player in the cache and database"
			},
			"OnlinePlayer": {
				"allOf": [
					{
						"$ref": "#/components/schemas/Player"
					},
					{
						"type": "object",
						"properties": {
							"afk_message": {
								"type": "string",
								"description": "The AFK message of the player or an empty string"
							},
							"main_character": {
								"type": "string",
								"description": "The name of the main character, or the same as $name if this is the main character of the player"
							},
							"online": {
								"type": "boolean",
								"description": "True if this player is currently online, false otherwise"
							}
						},
						"description": "This represents a single player in the online list"
					}
				]
			},
			"OnlinePlayers": {
				"type": "object",
				"properties": {
					"org": {
						"type": "array",
						"description": "All players online in the org",
						"items": {
							"$ref": "#/components/schemas/OnlinePlayer"
						}
					},
					"private_channel": {
						"type": "array",
						"description": "All players online in the private channel",
						"items": {
							"$ref": "#/components/schemas/OnlinePlayer"
						}
					}
				},
				"description": "This is the list of all players considered to be online by the bot"
			},
			"OrgHistory": {
				"type": "object",
				"properties": {
					"id": {
						"type": "integer",
						"description": "Internal ID of this history entry"
					},
					"actor": {
						"type": "string",
						"nullable": true,
						"description": "The person doing the action"
					},
					"actee": {
						"type": "string",
						"nullable": true,
						"description": "Optional, the person the actor is acting on"
					},
					"action": {
						"type": "string",
						"nullable": true,
						"description": "The action the actor is doing"
					},
					"organization": {
						"type": "string",
						"nullable": true,
						"description": "Name of the organization this action was done in"
					},
					"time": {
						"type": "integer",
						"nullable": true,
						"description": "Timestamp when the action happened"
					}
				}
			},
			"CmdPermSetMapping": {
				"type": "object",
				"properties": {
					"permission_set": {
						"type": "string",
						"description": "The permission set to map $source to"
					},
					"source": {
						"type": "string",
						"description": "The command source to map"
					},
					"symbol": {
						"type": "string",
						"description": "The symbol that triggers a command if it's the first letter"
					},
					"symbol_optional": {
						"type": "boolean",
						"description": "Is the symbol required to interpret the msg as command or optional"
					},
					"feedback": {
						"type": "boolean",
						"description": "Shall we report an error if the command doesn't exist"
					}
				}
			},
			"CmdPermissionSet": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string"
					},
					"letter": {
						"type": "string"
					}
				}
			},
			"ExtCmdPermissionSet": {
				"allOf": [
					{
						"$ref": "#/components/schemas/CmdPermissionSet"
					},
					{
						"type": "object",
						"properties": {
							"mappings": {
								"type": "array",
								"items": {
									"$ref": "#/components/schemas/CmdPermSetMapping"
								}
							}
						}
					}
				]
			},
			"EventType": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "The name of the event"
					},
					"description": {
						"type": "string",
						"nullable": true,
						"description": "The optional description, explaining when it occurs"
					}
				}
			},
			"FunctionParameter": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string"
					},
					"type": {
						"type": "string"
					},
					"description": {
						"type": "string",
						"nullable": true
					},
					"required": {
						"type": "boolean"
					}
				}
			},
			"ClassSpec": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string"
					},
					"class": {
						"type": "string"
					},
					"params": {
						"type": "array",
						"items": {
							"$ref": "#/components/schemas/FunctionParameter"
						}
					},
					"description": {
						"type": "string",
						"nullable": true
					}
				}
			},
			"RelayLayerArgument": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "The name of the argument"
					},
					"value": {
						"type": "string",
						"description": "The value of the argument"
					}
				}
			},
			"RelayLayer": {
				"type": "object",
				"properties": {
					"layer": {
						"type": "string",
						"description": "Which relay stack layer does this represent?"
					},
					"arguments": {
						"type": "array",
						"items": {
							"$ref": "#/components/schemas/RelayLayerArgument"
						}
					}
				}
			},
			"RelayEvent": {
				"type": "object",
				"properties": {
					"event": {
						"type": "string",
						"description": "Which event is this for?"
					},
					"incoming": {
						"type": "boolean",
						"description": "Allow sending the event via this relay?"
					},
					"outgoing": {
						"type": "boolean",
						"description": "Allow receiving the event via this relay?"
					}
				}
			},
			"RelayConfig": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "The name of this relay"
					},
					"layers": {
						"type": "array",
						"description": "The individual layers that make up this relay",
						"items": {
							"$ref": "#/components/schemas/RelayLayer"
						}
					},
					"events": {
						"type": "array",
						"description": "A list of events this relay allows in- and/or outbound",
						"items": {
							"$ref": "#/components/schemas/RelayEvent"
						}
					}
				}
			},
			"RelayStatus": {
				"type": "object",
				"properties": {
					"type": {
						"type": "string"
					},
					"text": {
						"type": "string"
					}
				}
			},
			"NewsTile": {
				"type": "object",
				"properties": {
					"name": {
						"type": "string",
						"description": "The name of this news tile"
					},
					"description": {
						"type": "string",
						"description": "A description what this news tile shows"
					},
					"example": {
						"type": "string",
						"nullable": true,
						"description": "An example what this could look like if there was data"
					}
				}
			},
			"BasicSystemInformation": {
				"type": "object",
				"properties": {
					"bot_name": {
						"type": "string",
						"description": "Name of the bot character in AO"
					},
					"superadmins": {
						"type": "array",
						"description": "Name(s) of the character(s) running the bot, empty if not set",
						"items": {
							"type": "string"
						}
					},
					"org": {
						"type": "string",
						"nullable": true,
						"description": "Name of the org this bot is in or null if not in an org"
					},
					"org_id": {
						"type": "integer",
						"nullable": true,
						"description": "ID of the org this bot is in or null if not in an org"
					},
					"bot_version": {
						"type": "string",
						"description": "Which Nadybot version are we running?"
					},
					"php_version": {
						"type": "string",
						"description": "Which PHP version are we running?"
					},
					"os": {
						"type": "string",
						"description": "Which operating system/kernel are we running?"
					},
