		"/audit": {
			"get": {
				"security": [
					{
						"basicAuth": []
					}
				],
				"description": "Query entries from the audit log",
				"responses": {
					"200": {
						"description": "The audit log entries",
						"content": {
							"application/json": {
								"schema": {
									"type": "array",
									"items": {
										"$ref": "#/components/schemas/Audit"
									}
								}
							}
						}
					}
				},
				"tags": [
					"security",
					"audit"
				]
			},
			"parameters": [
				{
					"name": "limit",
					"required": false,
					"in": "query",
					"schema": {
						"type": "integer"
					},
					"description": "No more than this amount of entries will be returned. Default is 50"
				},
				{
					"name": "offset",
					"required": false,
					"in": "query",
					"schema": {
						"type": "integer"
					},
					"description": "How many entries to skip before beginning to return entries"
				},
				{
					"name": "actor",
					"required": false,
					"in": "query",
					"schema": {
						"type": "string"
					},
					"description": "Show only entries of this actor"
				},
				{
					"name": "actee",
					"required": false,
					"in": "query",
					"schema": {
						"type": "string"
					},
					"description": "Show only entries with this actee"
				},
				{
					"name": "action",
					"required": false,
					"in": "query",
					"schema": {
						"type": "string"
					},
					"description": "Show only entries with this action"
				},
				{
					"name": "before",
					"required": false,
					"in": "query",
					"schema": {
						"type": "integer"
					},
					"description": "Show only entries from before the given timestamp"
				},
				{
					"name": "after",
					"required": false,
					"in": "query",
					"schema": {
						"type": "integer"
					},
					"description": "Show only entries from after the given timestamp"
				}
			]
		},
