    "/api/v1/goods": {
      "get": {
        "description": "GET list of goods.",
        "summary": "GET list of goods.",
        "operationId": "index",
        "tags": [
          "Goods"
        ],
        "parameters": [
          {
            "name": "Token",
            "in": "header",
            "required": true,
            "description": "user token",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "view",
            "in": "query",
            "required": false,
            "description": "请求来自的视图，允许值：<br/>1/ 所有物品 (default): all<br/>2/ 上线物品: online<br/>3/ 下线物品: offline",
            "schema": {
              "type": "string",
              "enum": [
                "all",
                "online",
                "offline"
              ],
              "default": "all"
            }
          },
          {
            "name": "search_type",
            "in": "query",
            "required": false,
            "description": "搜索的字段名，允许值：<br/>1/ name<br/>2/ creator<br/>3/ category_name<br/>4/ unit<br/>5/ price",
            "schema": {
              "type": "string",
              "enum": [
                "name",
                "creator",
                "category_name",
                "unit",
                "price"
              ],
              "as": "field"
            }
          },
          {
            "name": "value",
            "in": "query",
            "required": false,
            "description": "查询词",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "created_start_at",
            "in": "query",
            "required": false,
            "description": "时间起点, YY-MM-DD (HH:MM:SS, 可选)",
            "schema": {
              "type": "string",
              "format": "date-time",
              "as": "start"
            }
          },
          {
            "name": "created_end_at",
            "in": "query",
            "required": false,
            "description": "时间终点, YY-MM-DD (HH:MM:SS, 可选)",
            "schema": {
              "type": "string",
              "format": "date-time",
              "as": "end"
            }
          },
          {
            "name": "page",
            "in": "query",
            "required": false,
            "description": "页数，从 1 开始",
            "schema": {
              "type": "integer",
              "minimum": 1,
              "default": 1
            }
          },
          {
            "name": "rows",
            "in": "query",
            "required": false,
            "description": "per page, 请求的数据条数",
            "schema": {
              "type": "integer",
              "minimum": 1,
              "default": 10
            }
          },
          {
            "name": "export",
            "in": "query",
            "required": false,
            "description": "是否将查询结果导出 Excel 文件",
            "schema": {
              "type": "boolean",
              "examples": [
                {
                  "right_input": {
                    "value": true
                  }
                },
                {
                  "wrong_input": {
                    "value": "wrong input"
                  }
                }
              ]
            }
          }
        ],
        "responses": {
          "200": {
            "description": "success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "integer",
                      "default": 200
                    },
                    "msg": {
                      "type": "string",
                      "default": "success"
                    },
                    "total": {
                      "type": "integer"
                    },
                    "timestamp": {
                      "type": "integer",
                      "format": "time",
                      "is": "time"
                    },
                    "language": {
                      "type": "string",
                      "default": "Ruby"
                    },
                    "data": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "integer"
                          },
                          "name": {
                            "type": "string"
                          },
                          "unit": {
                            "type": "string"
                          },
                          "price": {
                            "type": "number",
                            "format": "float"
                          },
                          "remarks": {
                            "type": "string"
                          },
                          "pic_path": {
                            "type": "string"
                          },
                          "creator": {
                            "type": "string"
                          },
                          "is_online": {
                            "type": "boolean"
                          },
                          "created_at": {
                            "type": "string",
                            "format": "date-time"
                          },
                          "updated_at": {
                            "type": "string",
                            "format": "date-time"
                          },
                          "category_info": {
                            "type": "object",
                            "properties": {
                              "id": {
                                "type": "integer"
                              },
                              "name": {
                                "type": "string"
                              },
                              "base_category_id": {
                                "type": "integer"
                              },
                              "icon_name": {
                                "type": "string"
                              },
                              "deleted_at": {
                                "type": "string",
                                "format": "date-time"
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "-1": {
            "description": "invalid token"
          },
          "-10": {
            "description": "role verification failed"
          },
          "-11": {
            "description": "insufficient permission"
          },
          "400": {
            "description": "parameter validation failed"
          }
        },
        "examples": [
          {
            "right_input": {
              "value": {
                "Token": "Token",
                "view": "view",
                "search_type": "search_type",
                "value": "value",
                "created_start_at": "created_start_at",
                "created_end_at": "created_end_at",
                "page": "page",
                "rows": "rows",
                "export": "export"
              }
            }
          },
          {
            "wrong_input": {
              "value": {
                "Token": null,
                "view": null,
                "search_type": null,
                "value": null,
                "created_start_at": null,
                "created_end_at": null,
                "page": null,
                "rows": null,
                "export": null
              }
            }
          }
        ]
      },
      "post": {
        "summary": "POST create a good.",
        "operationId": "create",
        "tags": [
          "Goods"
