            "multipart/form-data": {
              "schema": {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "名字"
                  },
                  "category_id": {
                    "type": "integer",
                    "minimum": 1,
                    "as": "cate",
                    "not_permit": true,
                    "description": "子类 id"
                  },
                  "unit": {
                    "type": "string",
                    "description": "单位"
                  },
                  "price": {
                    "type": "number",
                    "format": "float",
                    "minimum": 0,
                    "description": "单价"
                  },
                  "is_online": {
                    "type": "boolean",
                    "description": "是否上线?"
                  },
                  "remarks": {
                    "type": "string",
                    "description": "其他说明"
                  },
                  "pic_path": {
                    "type": "string",
                    "format": "url",
                    "is": "url",
                    "description": "图片路径"
                  }
                },
                "required": [
                  "name",
                  "category_id",
                  "unit",
                  "price"
                ]
              },
              "examples": [
                {
                  "right_input": {
                    "value": {
                      "name": "good1",
                      "category_id": 6,
                      "unit": "unit",
                      "price": 5.7
                    }
                  }
                },
                {
                  "wrong_input": {
                    "value": {
                      "name": "good2",
                      "category_id": 0,
                      "unit": "unit",
                      "price": -1
                    }
                  }
                }
              ]
            }
          }
        },
        "responses": {
          "200": {
            "description": "success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "integer",
                      "default": 200
                    },
                    "msg": {
                      "type": "string",
                      "default": "success"
                    },
                    "total": {
                      "type": "integer"
                    },
                    "timestamp": {
                      "type": "integer",
                      "format": "time",
                      "is": "time"
                    },
                    "language": {
                      "type": "string",
                      "default": "Ruby"
                    },
                    "data": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          },
          "600": {
            "description": "create failed"
          },
          "-1": {
            "description": "invalid token"
          },
          "-10": {
            "description": "role verification failed"
          },
          "-11": {
            "description": "insufficient permission"
          },
          "400": {
            "description": "parameter validation failed"
          }
        }
      }
    },
