    "/AddToCollection": {
      "post": {
        "description": "Add an item to a collection",
        "operationId": "AddToCollection",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "example": {
                  "index": "0",
                  "input": [
                    "a",
                    "b",
                    "c"
                  ],
                  "item": "d",
                  "items": ""
                },
                "properties": {
                  "index": {
                    "description": "Index position for operation (leave blank to specify end of collection)",
                    "type": "string",
                    "x-ms-summary": "Index",
                    "in": "REQUEST_BODY"
                  },
                  "input": {
                    "description": "Collection of values or objects to modify",
                    "items": {
                      "type": "string",
                      "in": "REQUEST_BODY"
                    },
                    "type": "array",
                    "x-ms-summary": "Collection",
                    "required": true,
                    "in": "REQUEST_BODY"
                  },
                  "item": {
                    "description": "Item (for multiple items, leave blank and use Items)",
                    "type": "string",
                    "x-ms-summary": "Item",
                    "in": "REQUEST_BODY"
                  },
                  "items": {
                    "description": "Items (Collection, for a single item leave blank and use Item)",
                    "items": {
                      "type": "string",
                      "in": "REQUEST_BODY"
                    },
                    "type": "array",
                    "x-ms-summary": "Items",
                    "in": "REQUEST_BODY"
                  }
                },
                "type": "object",
                "x-schemaName": "inputCollectionModify",
                "in": "request_body"
              }
            }
          },
          "description": "Collection modification parameters",
          "required": false
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "example": {
                    "result": [
                      "a",
                      "b",
                      "c"
                    ]
                  },
                  "properties": {
                    "result": {
                      "description": "Modified collection result",
                      "items": {
                        "type": "string",
                        "in": "RESPONSE_BODY"
                      },
                      "type": "array",
                      "x-ms-summary": "Modified collection result",
                      "in": "RESPONSE_BODY"
                    }
                  },
                  "type": "object",
                  "x-schemaName": "outputCollectionResult",
                  "required": true,
                  "in": "response_body"
