                      "cpe_uri": "cpe:/a:microsoft:office:2000:::zh",
                      "cpe_uri_format": "2.2"
                    },
                    {
                      "product_version": "2000",
                      "cpe_uri": "cpe:/a:microsoft:office:2000:sp1",
                      "cpe_uri_format": "2.2"
                    },
                    {
                      "product_version": "2021",
                      "cpe_uri": "cpe:/a:microsoft:office:2021::~~ltsc~macos~~",
                      "cpe_uri_format": "2.2"
                    },
                    {
                      "product_version": "2004",
                      "cpe_uri": "cpe:/a:microsoft:office:2004::~~~macos~~",
                      "cpe_uri_format": "2.2"
                    },
                    {
                      "product_version": "2008",
                      "cpe_uri": "cpe:/a:microsoft:office:2008::~~~macos~~",
