    "/forms/526": {
      "get": {
        "summary": "Get a 526 schema for a claim.",
        "deprecated": true,
        "tags": [
          "Disability"
        ],
        "operationId": "GET:/forms/526",
        "description": "Returns a single 526 schema to automatically generate a form. Using this GET endpoint allows users to download our current validations.\n",
        "responses": {
          "200": {
            "description": "schema response",
            "content": {
              "application/json": {
                "example": {
                  "data": [
                    {
                      "$schema": "http://json-schema.org/draft-07/schema#",
                      "description": "Claims API 526 Schema",
                      "type": "object",
                      "additionalProperties": false,
                      "required": [
                        "veteran",
                        "serviceInformation",
                        "disabilities",
                        "claimantCertification",
                        "standardClaim",
                        "autoCestPDFGenerationDisabled"
                      ],
                      "properties": {
                        "veteran": {
                          "description": "Veteran Object being submitted in Claim",
                          "type": "object",
                          "additionalProperties": false,
                          "required": [
                            "currentMailingAddress",
                            "currentlyVAEmployee"
                          ],
                          "properties": {
                            "flashes": {
                              "description": "Attributes that describe special circumstances which apply to a Veteran.",
                              "type": "array",
                              "items": {
                                "type": "string",
                                "enum": [
                                  "Agent Orange - Vietnam",
                                  "Amyotrophic Lateral Sclerosis",
                                  "Blind",
                                  "Blue Water Navy",
                                  "Formerly Homeless",
                                  "GW Undiagnosed Illness",
                                  "Hardship",
                                  "Homeless",
                                  "Medal of Honor",
                                  "POW",
                                  "Priority Processing - Veteran over age 85",
                                  "Purple Heart",
                                  "Seriously Injured/Very Seriously Injured",
                                  "Specially Adapted Housing Claimed",
                                  "Terminally Ill"
                                ]
                              },
                              "example": "Hardship"
                            },
                            "currentlyVAEmployee": {
                              "type": "boolean",
                              "description": "Flag if Veteran is VA Employee.",
                              "example": false
                            },
                            "currentMailingAddress": {
                              "description": "Current Mailing Address Object being submitted.",
                              "type": "object",
                              "additionalProperties": false,
                              "properties": {
                                "addressLine1": {
                                  "description": "Address Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                  "maxLength": 20,
                                  "example": "1234 Couch Street"
                                },
                                "addressLine2": {
                                  "description": "Additional Address Information Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                  "maxLength": 20,
                                  "example": "Apt. 22"
                                },
                                "addressLine3": {
                                  "description": "Additional Address Information Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                  "maxLength": 20,
                                  "example": "Box 123"
                                },
                                "city": {
                                  "description": "City Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.#]([-a-zA-Z0-9'.# ])?)+$",
                                  "example": "Portland"
                                },
                                "country": {
                                  "description": "Country Veteran resides in.",
                                  "type": "string",
                                  "example": "USA"
                                },
                                "zipFirstFive": {
                                  "description": "Zipcode (First 5 digits) Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^\\d{5}?$",
                                  "example": "12345"
                                },
                                "zipLastFour": {
                                  "description": "Zipcode (Last 4 digits) Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^\\d{4}?$",
                                  "example": "6789"
                                },
                                "internationalPostalCode": {
                                  "type": "string"
                                },
                                "type": {
                                  "description": "Type of residence Veteran resides in.  \n If value is \"INTERNATIONAL\", then \"addressLine1\", \"city\", \"country\", and \"internationalPostalCode\" are required.  \n If value is \"DOMESTIC\", then \"addressLine1\", \"city\", \"state\", \"country\", and \"zipFirstFive\" are required.  \n If value is \"MILITARY\", then \"addressLine1\", \"country\", \"militaryStateCode\", \"militaryPostOfficeTypeCode\", and \"zipFirstFive\" are required.",
                                  "type": "string",
                                  "default": "DOMESTIC",
                                  "enum": [
                                    "INTERNATIONAL",
                                    "MILITARY",
                                    "DOMESTIC"
                                  ],
                                  "example": "DOMESTIC"
                                },
                                "state": {
                                  "description": "State Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^[a-z,A-Z]{2}$",
                                  "example": "OR"
                                }
                              },
                              "allOf": [
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "INTERNATIONAL"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "addressLine1",
                                      "city",
                                      "country",
                                      "type",
                                      "internationalPostalCode"
                                    ]
                                  }
                                },
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "DOMESTIC"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "addressLine1",
                                      "city",
                                      "country",
                                      "zipFirstFive",
                                      "type",
                                      "state"
                                    ]
                                  }
                                },
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "MILITARY"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "addressLine1",
                                      "country",
                                      "zipFirstFive",
                                      "militaryStateCode",
                                      "militaryPostOfficeTypeCode",
                                      "type"
                                    ]
                                  }
                                }
                              ]
                            },
                            "changeOfAddress": {
                              "description": "A Change of Address Object being submitted.",
                              "type": "object",
                              "additionalProperties": false,
                              "required": [
                                "country",
                                "type",
                                "addressLine1",
                                "addressChangeType",
                                "beginningDate"
                              ],
                              "properties": {
                                "beginningDate": {
                                  "description": "Date in YYYY-MM-DD the Veteran changed address. Date must be in the future if change is temporary.",
                                  "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$",
                                  "type": "string",
                                  "example": "2018-06-04"
                                },
                                "endingDate": {
                                  "description": "Date in YYYY-MM-DD the changed address expires, if change is temporary.",
                                  "type": "string",
                                  "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$",
                                  "example": "2018-06-04"
                                },
                                "addressChangeType": {
                                  "description": "Temporary or Permanent change of address.",
                                  "type": "string",
                                  "example": "PERMANENT"
                                },
                                "addressLine1": {
                                  "description": "New Address Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                  "maxLength": 20,
                                  "example": "1234 Couch Street"
                                },
                                "addressLine2": {
                                  "description": "New Additional Address Information Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                  "maxLength": 20,
                                  "example": "Apt. 22"
                                },
                                "city": {
                                  "description": "New city Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^([-a-zA-Z0-9'.#]([-a-zA-Z0-9'.# ])?)+$",
                                  "example": "Portland"
                                },
                                "country": {
                                  "description": "New country Veteran resides in.",
                                  "type": "string",
                                  "example": "USA"
                                },
                                "zipFirstFive": {
                                  "description": "New zipcode (First 5 digits) Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^\\d{5}?$",
                                  "example": "12345"
                                },
                                "zipLastFour": {
                                  "description": "New zipcode (Last 4 digits) Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^\\d{4}?$",
                                  "example": "6789"
                                },
                                "internationalPostalCode": {
                                  "type": "string"
                                },
                                "type": {
                                  "description": "New type of residence Veteran resides in.  \n If value is \"INTERNATIONAL\", then \"city\" and \"internationalPostalCode\" are required.  \n If value is \"DOMESTIC\", then \"city\", \"state\", and \"zipFirstFive\" are required.  \n If value is \"MILITARY\", then \"militaryStateCode\", \"militaryPostOfficeTypeCode\", and \"zipFirstFive\" are required.",
                                  "type": "string",
                                  "default": "DOMESTIC",
                                  "enum": [
                                    "INTERNATIONAL",
                                    "MILITARY",
                                    "DOMESTIC"
                                  ],
                                  "example": "DOMESTIC"
                                },
                                "militaryStateCode": {
                                  "type": "string"
                                },
                                "militaryPostOfficeTypeCode": {
                                  "type": "string"
                                },
                                "state": {
                                  "description": "New state Veteran resides in.",
                                  "type": "string",
                                  "pattern": "^[a-z,A-Z]{2}$",
                                  "example": "OR"
                                }
                              },
                              "allOf": [
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "INTERNATIONAL"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "city",
                                      "internationalPostalCode"
                                    ]
                                  }
                                },
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "DOMESTIC"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "city",
                                      "zipFirstFive",
                                      "state"
                                    ]
                                  }
                                },
                                {
                                  "if": {
                                    "properties": {
                                      "type": {
                                        "const": "MILITARY"
                                      }
                                    }
                                  },
                                  "then": {
                                    "required": [
                                      "zipFirstFive",
                                      "militaryStateCode",
                                      "militaryPostOfficeTypeCode"
                                    ]
                                  }
                                }
                              ]
                            },
                            "homelessness": {
                              "description": "Object describing Veteran Homelessness if applicable.",
                              "type": "object",
                              "additionalProperties": false,
                              "properties": {
                                "pointOfContact": {
                                  "description": "Object describing Homeless Veteran Point of Contact.",
                                  "type": "object",
                                  "additionalProperties": false,
                                  "required": [
                                    "pointOfContactName",
                                    "primaryPhone"
                                  ],
                                  "properties": {
                                    "pointOfContactName": {
                                      "description": "Point of contact in direct contact with Veteran.",
                                      "type": "string",
                                      "minLength": 1,
                                      "maxLength": 100,
                                      "pattern": "^([-a-zA-Z0-9/']+( ?))*$",
                                      "example": "Jane Doe"
                                    },
                                    "primaryPhone": {
                                      "description": "Phone Number Object for Point of Contact.",
                                      "type": "object",
                                      "additionalProperties": false,
                                      "required": [
                                        "areaCode",
                                        "phoneNumber"
                                      ],
                                      "properties": {
                                        "areaCode": {
                                          "description": "Area code of Point of Contact.",
                                          "type": "string",
                                          "example": "123"
                                        },
                                        "phoneNumber": {
                                          "description": "Primary phone of Point of Contact.",
                                          "type": "string",
                                          "example": "1231234"
                                        }
                                      }
                                    }
                                  }
                                },
                                "currentlyHomeless": {
                                  "type": "object",
                                  "additionalProperties": false,
                                  "required": [
                                    "homelessSituationType",
                                    "otherLivingSituation"
