                              "items": {
                                "type": "object",
                                "required": [
                                  "serviceBranch",
                                  "activeDutyBeginDate",
                                  "activeDutyEndDate"
                                ],
                                "properties": {
                                  "serviceBranch": {
                                    "description": "Branch of Service during period.",
                                    "type": "string",
                                    "example": "Air Force",
                                    "enum": [
                                      "Air Force",
                                      "Air Force Academy",
                                      "Air Force Reserves",
                                      "Air Force Civilian",
                                      "Air National Guard",
                                      "Army",
                                      "Army Air Corps",
                                      "Army Air Corps or Army Air Force",
                                      "Army National Guard",
                                      "Army Nurse Corps",
                                      "Army Reserves",
                                      "Coast Guard",
                                      "Coast Guard Academy",
                                      "Coast Guard Reserves",
                                      "Commonwealth Army Veteran",
                                      "Guerrilla Combination Service",
                                      "Marine",
                                      "Marine Corps",
                                      "Marine Corps Reserves",
                                      "Merchant Marine",
                                      "National Oceanic and Atmospheric Administration",
                                      "Naval Academy",
                                      "Navy",
                                      "Navy Reserves",
                                      "Other",
                                      "Public Health Service",
                                      "Regular Philippine Scout",
                                      "Regular Scout Service",
                                      "Special Philippine Scout",
                                      "Unknown",
                                      "US Military Academy",
                                      "Woman Air Corps",
                                      "Women's Army Corps"
                                    ]
                                  },
                                  "activeDutyBeginDate": {
                                    "description": "Date Started Active Duty.",
                                    "type": "string",
                                    "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$",
                                    "example": "1980-02-05"
                                  },
                                  "activeDutyEndDate": {
                                    "description": "Date Completed Active Duty.  If in the future, 'separationLocationCode' is required.",
                                    "type": "string",
                                    "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$",
                                    "example": "1990-01-02"
                                  },
                                  "separationLocationCode": {
                                    "description": "Code for the facility the Veteran plans to separate from.  Required if 'servicePeriod.activeDutyEndDate' is in the future. [Code values available here.](https://github.com/department-of-veterans-affairs/vets-api/blob/master/modules/claims_api/lib/claims_api/separation_location_code_values.json)",
                                    "type": "string",
                                    "example": "98283"
                                  }
                                }
                              }
                            },
                            "confinements": {
                              "description": "Used if the Veteran was ever a prisoner of war.",
                              "type": "array",
                              "minItems": 1,
                              "uniqueItems": true,
                              "items": {
                                "type": "object",
                                "additionalProperties": false,
                                "required": [
                                  "confinementBeginDate",
                                  "confinementEndDate"
                                ],
                                "properties": {
                                  "confinementBeginDate": {
                                    "type": "string",
                                    "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$"
                                  },
                                  "confinementEndDate": {
                                    "type": "string",
                                    "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$"
                                  }
                                }
                              }
                            },
                            "reservesNationalGuardService": {
                              "type": "object",
                              "additionalProperties": false,
                              "required": [
                                "obligationTermOfServiceFromDate",
                                "obligationTermOfServiceToDate",
                                "unitName"
                              ],
                              "properties": {
                                "title10Activation": {
                                  "description": "Include if the Veteran is currently activated on federal orders within the National Guard or Reserves",
                                  "type": "object",
                                  "additionalProperties": false,
                                  "required": [
                                    "anticipatedSeparationDate",
                                    "title10ActivationDate"
                                  ],
                                  "properties": {
                                    "anticipatedSeparationDate": {
                                      "description": "Anticipated date of separation. Date must be in the future.",
                                      "type": "string",
                                      "pattern": "^(\\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$"
                                    },
                                    "title10ActivationDate": {
                                      "description": "Date cannot be in the future and must be after the earliest servicePeriod.activeDutyBeginDate.",
