                        "required": [
                          "veteran",
                          "serviceInformation",
                          "disabilities",
                          "claimantCertification",
                          "standardClaim",
                          "autoCestPDFGenerationDisabled"
                        ],
                        "properties": {
                          "veteran": {
                            "description": "Veteran Object being submitted in Claim",
                            "type": "object",
                            "additionalProperties": false,
                            "required": [
                              "currentMailingAddress",
                              "currentlyVAEmployee"
                            ],
                            "properties": {
                              "flashes": {
                                "description": "Attributes that describe special circumstances which apply to a Veteran.",
                                "type": "array",
                                "items": {
                                  "type": "string",
                                  "enum": [
                                    "Agent Orange - Vietnam",
                                    "Amyotrophic Lateral Sclerosis",
                                    "Blind",
                                    "Blue Water Navy",
                                    "Formerly Homeless",
                                    "GW Undiagnosed Illness",
                                    "Hardship",
                                    "Homeless",
                                    "Medal of Honor",
                                    "POW",
                                    "Priority Processing - Veteran over age 85",
                                    "Purple Heart",
                                    "Seriously Injured/Very Seriously Injured",
                                    "Specially Adapted Housing Claimed",
                                    "Terminally Ill"
                                  ]
                                },
                                "example": "Hardship"
                              },
                              "currentlyVAEmployee": {
                                "type": "boolean",
                                "description": "Flag if Veteran is VA Employee.",
                                "example": false
                              },
                              "currentMailingAddress": {
                                "description": "Current Mailing Address Object being submitted.",
                                "type": "object",
                                "additionalProperties": false,
                                "properties": {
                                  "addressLine1": {
                                    "description": "Address Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                    "maxLength": 20,
                                    "example": "1234 Couch Street"
                                  },
                                  "addressLine2": {
                                    "description": "Additional Address Information Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                    "maxLength": 20,
                                    "example": "Apt. 22"
                                  },
                                  "addressLine3": {
                                    "description": "Additional Address Information Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^([-a-zA-Z0-9'.,&#]([-a-zA-Z0-9'.,&# ])?)+$",
                                    "maxLength": 20,
                                    "example": "Box 123"
                                  },
                                  "city": {
                                    "description": "City Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^([-a-zA-Z0-9'.#]([-a-zA-Z0-9'.# ])?)+$",
                                    "example": "Portland"
                                  },
                                  "country": {
                                    "description": "Country Veteran resides in.",
                                    "type": "string",
                                    "example": "USA"
                                  },
                                  "zipFirstFive": {
                                    "description": "Zipcode (First 5 digits) Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^\\d{5}?$",
                                    "example": "12345"
                                  },
                                  "zipLastFour": {
                                    "description": "Zipcode (Last 4 digits) Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^\\d{4}?$",
                                    "example": "6789"
                                  },
                                  "internationalPostalCode": {
                                    "type": "string"
                                  },
                                  "type": {
                                    "description": "Type of residence Veteran resides in.  \n If value is \"INTERNATIONAL\", then \"addressLine1\", \"city\", \"country\", and \"internationalPostalCode\" are required.  \n If value is \"DOMESTIC\", then \"addressLine1\", \"city\", \"state\", \"country\", and \"zipFirstFive\" are required.  \n If value is \"MILITARY\", then \"addressLine1\", \"country\", \"militaryStateCode\", \"militaryPostOfficeTypeCode\", and \"zipFirstFive\" are required.",
                                    "type": "string",
                                    "default": "DOMESTIC",
                                    "enum": [
                                      "INTERNATIONAL",
                                      "MILITARY",
                                      "DOMESTIC"
                                    ],
                                    "example": "DOMESTIC"
                                  },
                                  "state": {
                                    "description": "State Veteran resides in.",
                                    "type": "string",
                                    "pattern": "^[a-z,A-Z]{2}$",
                                    "example": "OR"
                                  }
                                },
                                "allOf": [
                                  {
                                    "if": {
                                      "properties": {
                                        "type": {
                                          "const": "INTERNATIONAL"
                                        }
                                      }
                                    },
                                    "then": {
                                      "required": [
                                        "addressLine1",
                                        "city",
                                        "country",
                                        "type",
                                        "internationalPostalCode"
                                      ]
                                    }
                                  },
                                  {
                                    "if": {
                                      "properties": {
                                        "type": {
                                          "const": "DOMESTIC"
                                        }
                                      }
                                    },
                                    "then": {
                                      "required": [
                                        "addressLine1",
                                        "city",
                                        "country",
                                        "zipFirstFive",
                                        "type",
                                        "state"
                                      ]
                                    }
                                  },
                                  {
                                    "if": {
                                      "properties": {
                                        "type": {
                                          "const": "MILITARY"
                                        }
                                      }
                                    },
                                    "then": {
                                      "required": [
                                        "addressLine1",
                                        "country",
                                        "zipFirstFive",
                                        "militaryStateCode",
                                        "militaryPostOfficeTypeCode",
                                        "type"
                                      ]
                                    }
                                  }
                                ]
                              },
                              "changeOfAddress": {
                                "description": "A Change of Address Object being submitted.",
                                "type": "object",
