{
  "openapi": "3.0.3",
  "info": {
    "title": "Alephium API",
    "version": "1.6.0"
  },
  "servers": [
    {
      "url": "../"
    },
    {
      "url": "{protocol}://{host}:{port}",
      "variables": {
        "protocol": {
          "enum": [
            "http",
            "https"
          ],
          "default": "http"
        },
        "host": {
          "default": "127.0.0.1"
        },
        "port": {
          "default": "12973"
        }
      }
    }
  ],
  "paths": {
    "/wallets": {
      "get": {
        "tags": [
          "Wallets"
        ],
        "summary": "List available wallets",
        "operationId": "getWallets",
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/WalletStatus"
                  }
                },
                "example": [
                  {
                    "walletName": "wallet-super-name",
                    "locked": true
                  }
                ]
              }
            }
          },
          "400": {
            "description": "BadRequest",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BadRequest"
                },
                "example": {
                  "detail": "Something bad in the request"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Unauthorized"
                },
                "example": {
                  "detail": "You shall not pass"
                }
              }
            }
          },
          "404": {
            "description": "NotFound",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/NotFound"
                },
                "example": {
                  "resource": "wallet-name",
                  "detail": "wallet-name not found"
                }
              }
            }
          },
          "500": {
            "description": "InternalServerError",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerError"
                },
                "example": {
                  "detail": "Ouch"
                }
              }
            }
          },
          "503": {
            "description": "ServiceUnavailable",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailable"
                },
                "example": {
                  "detail": "Self clique unsynced"
                }
              }
            }
          }
        }
      },
      "put": {
        "tags": [
          "Wallets"
        ],
        "summary": "Restore a wallet from your mnemonic",
        "operationId": "putWallets",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/WalletRestore"
              },
              "examples": {
                "Example0": {
                  "summary": "User",
                  "value": {
                    "password": "my-secret-password",
                    "mnemonic": "vault alarm sad mass witness property virus style good flower rice alpha viable evidence run glare pretty scout evil judge enroll refuse another lava",
                    "walletName": "wallet-super-name"
                  }
                },
                "Example1": {
                  "summary": "Miner (w/o pass phrase)",
                  "value": {
                    "password": "my-secret-password",
                    "mnemonic": "vault alarm sad mass witness property virus style good flower rice alpha viable evidence run glare pretty scout evil judge enroll refuse another lava",
                    "walletName": "wallet-super-name",
                    "isMiner": true
                  }
                },
                "Example2": {
                  "summary": "Miner (with pass phrase)",
                  "value": {
                    "password": "my-secret-password",
                    "mnemonic": "vault alarm sad mass witness property virus style good flower rice alpha viable evidence run glare pretty scout evil judge enroll refuse another lava",
                    "walletName": "wallet-super-name",
                    "isMiner": true,
                    "mnemonicPassphrase": "optional-mnemonic-passphrase"
                  }
                }
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/WalletRestoreResult"
                },
                "example": {
                  "walletName": "wallet-super-name"
                }
              }
            }
          },
          "400": {
            "description": "BadRequest",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BadRequest"
                },
                "example": {
                  "detail": "Something bad in the request"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Unauthorized"
                },
                "example": {
                  "detail": "You shall not pass"
                }
              }
            }
          },
          "404": {
            "description": "NotFound",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/NotFound"
                },
                "example": {
                  "resource": "wallet-name",
                  "detail": "wallet-name not found"
                }
              }
            }
          },
          "500": {
            "description": "InternalServerError",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerError"
                },
                "example": {
                  "detail": "Ouch"
                }
              }
            }
          },
          "503": {
            "description": "ServiceUnavailable",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailable"
                },
                "example": {
                  "detail": "Self clique unsynced"
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "Wallets"
        ],
        "summary": "Create a new wallet",
        "description": "A new wallet will be created and respond with a mnemonic. Make sure to keep that mnemonic safely as it will allows you to recover your wallet. Default mnemonic size is 24, (options: 12, 15, 18, 21, 24).",
