                "Example1": {
                  "summary": "Miner (w/o pass phrase)",
                  "value": {
                    "password": "my-secret-password",
                    "walletName": "wallet-super-name",
                    "isMiner": true,
                    "mnemonicSize": 24
                  }
                },
                "Example2": {
                  "summary": "Miner (with pass phrase)",
                  "value": {
                    "password": "my-secret-password",
                    "walletName": "wallet-super-name",
                    "isMiner": true,
                    "mnemonicPassphrase": "optional-mnemonic-passphrase",
                    "mnemonicSize": 24
