{
  "openapi": "3.0.2",
  "info": {
    "title": "ProPresenter API",
    "version": "1.0",
    "description": "Public API for controlling and retrieving information from ProPresenter."
  },
  "servers": [
    {
      "url": "../../"
    }
  ],
  "paths": {
    "/v1/audio/playlists": {
      "get": {
        "operationId": "audioPlaylistsGet",
        "tags": [
          "Audio"
        ],
        "summary": "Requests a list with all the configured audio playlists.",
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "required": [
                      "id"
                    ],
                    "properties": {
                      "id": {
                        "$ref": "#/paths/~1v1~1playlist~1current/get/responses/200/content/application~1json/schema"
                      },
                      "type": {
                        "type": "string",
                        "enum": [
                          "group",
                          "playlist"
                        ],
                        "description": "Whether this is a playlist folder or an actual playlist."
                      },
                      "children": {
                        "type": "array",
                        "items": {
                          "$ref": "#/paths/~1v1~1audio~1playlists/get/responses/200/content/application~1json/schema/items"
                        },
                        "description": "The list of child playlists inside this playlist."
                      }
                    }
                  }
                },
                "example": {
                  "id": {
                    "uuid": "ED59503B-394D-4D5D-B0B0-5AE80D0BA025",
                    "name": "Playlist Folder",
                    "index": 0
                  },
                  "type": "group",
                  "children": [
                    {
                      "id": {
                        "uuid": "C49A250A-3E32-4C26-A220-46D6596426B5",
                        "name": "Playlist 1",
                        "index": 0
                      },
                      "type": "playlist",
                      "children": []
                    },
                    {
                      "id": {
                        "uuid": "D8C5D151-45C9-4CFF-B1F9-74F3B4B5173B",
                        "name": "Playlist 2",
                        "index": 1
                      },
                      "type": "playlist",
                      "children": []
                    }
                  ]
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/audio/playlist/{playlist_id}": {
      "get": {
        "operationId": "audioPlaylistGet",
        "tags": [
          "Audio"
        ],
        "summary": "Requests a list of all the audio items in the specified audio playlist.",
        "description": "A maximum of 100 items will be returned per call. The `start` query parameter can\nbe used to retrieve multiple pages of items, by setting it to the index of the first\nitem to return.\n",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1playlist~1%7Bplaylist_id%7D/parameters/0"
          },
          {
            "in": "query",
            "required": false,
            "name": "start",
            "description": "The starting index in the list. If the index is out of range, the returned items array will be empty.\n",
            "schema": {
              "type": "integer"
            },
            "example": 100,
            "default": 0
          }
        ],
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "required": [
                      "id",
                      "items"
                    ],
                    "properties": {
                      "id": {
                        "$ref": "#/paths/~1v1~1playlist~1current/get/responses/200/content/application~1json/schema"
                      },
                      "items": {
                        "type": "array",
                        "$ref": "#/paths/~1v1~1media~1playlist~1%7Bplaylist_id%7D/get/responses/200/content/application~1json/schema/items"
                      }
                    }
                  }
                },
                "example": {
                  "id": {
                    "uuid": "B238C071-3628-4204-BB19-DDE76DC57CE7",
                    "name": "Playlist 3",
                    "index": 2
                  },
                  "items": [
                    {
                      "id": {
                        "uuid": "94A2D4EA-EEA5-48A4-AF6F-850D700C1830",
                        "name": "Song 1",
                        "index": 0
                      },
                      "type": "audio",
                      "artist": "unknown",
                      "duration": 123
                    },
                    {
                      "id": {
                        "uuid": "8F40D44E-4018-4552-8803-60DC788DB1CF",
                        "name": "Amazing Grace",
                        "index": 1
                      },
                      "type": "audio",
                      "artist": "John Newton",
                      "duration": 110
                    }
                  ]
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/audio/playlist/{playlist_id}/updates": {
      "get": {
        "operationId": "audioPlaylistUpdateGet",
        "tags": [
          "Audio"
        ],
        "summary": "Requests a chunked data update every time the specified audio playlist changes.",
        "description": "This endpoint can be used to subscribe to changes to a particular playlist.\nThe response is chunked, where each chunk is a simple string \"change\".\nThis string will be sent whenever the audio playlist changes in regards to any of the\ndata returned by `GET /audio/playlist/{playlist_id}`. The updated playlist data can be\nqueried by recalling `GET /audio/playlist/{playlist_id}`.\n\nNote that this endpoint will return nothing unless the `chunked` parameter is set to `true`.\n",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1playlist~1%7Bplaylist_id%7D/parameters/0"
          },
          {
            "$ref": "#/paths/~1v1~1props/get/parameters/0"
          }
        ],
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "string",
                  "enum": [
                    "change"
                  ]
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/capture/status": {
      "get": {
        "operationId": "captureStatusGet",
        "tags": [
          "Capture"
        ],
        "summary": "Requests the current capture status and capture time.",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1props/get/parameters/0"
          }
        ],
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "required": [
                    "capturing"
                  ],
                  "properties": {
                    "capturing": {
                      "type": "boolean",
                      "description": "`true` if the capture is running, `false` otherwise\n"
                    },
                    "capture_time": {
                      "type": "string",
                      "description": "The length of time the capture has been running for, hh:mm:ss format",
                      "example": "00:42:27"
                    }
                  }
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/capture/{operation}": {
      "get": {
        "operationId": "captureOperation",
        "tags": [
          "Capture"
        ],
        "summary": "Performs the requested capture operation (start, stop).",
        "parameters": [
          {
            "in": "path",
            "required": true,
            "name": "operation",
            "description": "The capture operation to perform",
            "schema": {
              "type": "string",
              "enum": [
                "start",
                "stop"
              ]
            }
          }
        ],
        "responses": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/capture/settings": {
      "get": {
        "operationId": "captureSettingsGet",
        "tags": [
          "Capture"
        ],
        "summary": "Requests the current capture settings.",
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/paths/~1v1~1capture~1settings/put/requestBody/content/application~1json/schema"
                },
                "examples": {
                  "disk": {
                    "$ref": "#/paths/~1v1~1capture~1settings/put/requestBody/content/application~1json/examples/disk"
                  },
                  "rtmp_no_local": {
                    "$ref": "#/paths/~1v1~1capture~1settings/put/requestBody/content/application~1json/examples/rtmp_no_local"
                  },
                  "rtmp_local": {
                    "$ref": "#/paths/~1v1~1capture~1settings/put/requestBody/content/application~1json/examples/rtmp_local"
                  },
                  "resi": {
                    "$ref": "#/paths/~1v1~1capture~1settings/put/requestBody/content/application~1json/examples/resi"
                  }
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      },
      "put": {
        "operationId": "captureSettingsSet",
        "tags": [
          "Capture"
        ],
        "summary": "Sets the capture settings.",
        "requestBody": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "400": {
            "$ref": "#/paths/~1v1~1looks/post/responses/400"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          },
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "source",
                  "destination",
                  "audio_routing"
                ],
                "properties": {
                  "source": {
                    "type": "string",
                    "description": "The UUID of the source screen."
                  },
                  "audio_routing": {
                    "type": "array",
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "integer"
                      }
                    },
                    "description": "Audio routing for the capture stream."
                  },
                  "disk": {
                    "type": "object",
                    "required": [
                      "file_location",
                      "codec",
                      "resolution",
                      "frame_rate"
                    ],
                    "properties": {
                      "file_location": {
                        "type": "string",
                        "description": "The location of the file on disk (folder only, no filename)",
                        "example": "/Users/someuser/Movies"
                      },
                      "encoding": {
                        "type": "string",
                        "description": "The encoding used for encoding the captured video. This must match one\nof the encodings returned by the /encoding endpoint\n"
                      },
                      "resolution": {
                        "type": "object",
                        "required": [
                          "width",
                          "height"
                        ],
                        "properties": {
                          "width": {
                            "type": "integer",
                            "example": 1920
                          },
                          "height": {
                            "type": "integer",
                            "example": 1080
                          }
                        }
                      },
                      "frame_rate": {
                        "type": "number",
                        "description": "The capture frame rate.",
                        "example": 29.97
                      }
                    }
                  },
                  "rtmp": {
                    "type": "object",
                    "required": [
                      "url",
                      "key",
                      "encoding",
                      "save_local"
                    ],
                    "properties": {
                      "url": {
                        "type": "string",
                        "description": "The URL of the RTMP server.",
                        "example": "rtmps://server.com/live"
                      },
                      "key": {
                        "type": "string",
                        "description": "The stream key to use with the URL.",
                        "example": "g5sr2hz9"
                      },
                      "encoding": {
                        "type": "string",
                        "description": "The encoding used for encoding the captured video. This must match one\nof the encodings returned by the /encoding endpoint\n"
                      },
                      "save_local": {
                        "type": "boolean",
                        "description": "`true` to save a local copy of the stream, `false` otherwise\n"
                      },
                      "file_location": {
                        "type": "string",
                        "description": "The location of the file on disk (folder only, no filename)",
                        "example": "/Users/someuser/Movies"
                      }
                    }
                  },
                  "resi": {
                    "type": "object",
                    "required": [
                      "destination_group",
                      "encoding"
                    ],
                    "properties": {
                      "event_name": {
                        "type": "string",
                        "description": "Optional event name.",
                        "example": "My Event"
                      },
                      "event_description": {
                        "type": "string",
                        "description": "Optional event description.",
                        "example": "This is a short description of the event."
                      },
                      "destination_group": {
                        "type": "string",
                        "example": "Default Destination Group."
                      },
                      "encoding": {
                        "type": "string",
                        "description": "The encoding used for encoding the captured video. This must match one\nof the encodings returned by the /encoding endpoint\n"
                      }
                    }
                  }
                }
              },
              "examples": {
                "disk": {
                  "summary": "Capture to disk",
                  "value": {
                    "source": "3C39C433-5C18-4F51-B357-55BB870227C4",
                    "audio_routing": [
                      [
                        1
                      ],
                      [
                        2
                      ]
                    ],
                    "disk": {
                      "file_location": "/Users/someuser/Movies",
                      "codec": "h_264",
                      "resolution": {
                        "width": 1920,
                        "height": 1080
                      },
                      "frame_rate": 29.97
                    }
                  }
                },
                "rtmp_no_local": {
                  "summary": "Stream to RTMP (no local copy)",
                  "value": {
                    "source": "3C39C433-5C18-4F51-B357-55BB870227C4",
                    "audio_routing": [
                      [
                        1
                      ],
                      [
                        2
                      ]
                    ],
                    "rtmp": {
                      "server": "rtmps://server.com/live",
                      "key": "g5sr2hz9",
                      "encoding": "720p30_2_5",
                      "save_local": false
                    }
                  }
                },
                "rtmp_local": {
                  "summary": "Stream to RTMP (with local copy)",
                  "value": {
                    "source": "3C39C433-5C18-4F51-B357-55BB870227C4",
                    "destination": "disk",
                    "rtmp": {
                      "server": "rtmps://server.com/live",
                      "key": "g5sr2hz9",
                      "encoding": "720p30_2_5",
                      "save_local": true,
                      "file_location": "/Users/someuser/Movies"
                    }
                  }
                },
                "resi": {
                  "summary": "Stream to Resi",
                  "value": {
                    "source": "3C39C433-5C18-4F51-B357-55BB870227C4",
                    "destination": "disk",
                    "audio_routing": [
                      [
                        1
                      ],
                      [
                        2
                      ]
                    ],
                    "resi": {
                      "event_name": "My Event",
                      "event_description": "This is a short description of the event.",
                      "destination_group": "Default Destination Group",
                      "encoding": "720p30_4"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/capture/encodings/{type}": {
      "get": {
        "operationId": "captureModesGet",
        "tags": [
          "Capture"
        ],
        "summary": "Requests a list of all available capture modes for the capture type (disk, rtmp, resi).",
        "parameters": [
          {
            "in": "path",
            "required": true,
            "name": "type",
            "description": "The required capture type.",
            "schema": {
              "type": "string",
              "enum": [
                "disk",
                "rtmp",
                "resi"
              ]
            }
          }
        ],
        "responses": {
          "200": {
            "description": "The request was completed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "string"
                  }
                },
                "example": [
                  "h_264",
                  "h_265",
                  "hap",
                  "hap_alpha",
                  "hap_q",
                  "prores_422",
                  "prores_422_hq",
                  "prores_422_lt",
                  "prores_422_proxy",
                  "prores_4444",
                  "prores_4444_xq"
                ]
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/clear/layer/{layer}": {
      "get": {
        "operationId": "clearLayer",
        "tags": [
          "Clear"
        ],
        "summary": "Clears the specified layer (audio, props, messages, announcements, slide, media, video_input).",
        "parameters": [
          {
            "in": "path",
            "required": true,
            "name": "layer",
            "description": "The name of layer that is to be cleared.",
            "schema": {
              "type": "string",
              "enum": [
                "audio",
                "props",
                "messages",
                "announcements",
                "slide",
                "media",
                "video_input"
              ]
            }
          }
        ],
        "responses": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/clear/group/{id}": {
      "get": {
        "operationId": "clearGroupGet",
        "tags": [
          "Clear"
        ],
        "summary": "Requests the details of the specified clear group.",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/parameters/0"
          }
        ],
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/paths/~1v1~1clear~1groups/post/requestBody/content/application~1json/schema"
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      },
      "put": {
        "operationId": "clearGroupSet",
        "tags": [
          "Clear"
        ],
        "summary": "Sets the details of the specified clear group.",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/parameters/0"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/paths/~1v1~1clear~1groups/post/requestBody/content/application~1json/schema"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/paths/~1v1~1clear~1groups/post/requestBody/content/application~1json/schema"
                }
              }
            }
          },
          "400": {
            "$ref": "#/paths/~1v1~1looks/post/responses/400"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      },
      "delete": {
        "operationId": "clearGroupDelete",
        "tags": [
          "Clear"
        ],
        "summary": "Deletes the specified clear group.",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/parameters/0"
          }
        ],
        "responses": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/clear/group/{id}/icon": {
      "parameters": [
        {
          "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/parameters/0"
        }
      ],
      "get": {
        "operationId": "clearGroupIconGet",
        "tags": [
          "Clear"
        ],
        "summary": "Requests the image data for the icon of the specified clear group.",
        "responses": {
          "200": {
            "$ref": "#/paths/~1v1~1mask~1%7Bid%7D~1thumbnail/get/responses/200"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      },
      "put": {
        "operationId": "clearGroupIconSet",
        "tags": [
          "Clear"
        ],
        "summary": "Sets the custom icon of the specified clear group.",
        "requestBody": {
          "required": true,
          "content": {
            "image/*": {
              "schema": {
                "type": "string",
                "format": "binary"
              }
            }
          }
        },
        "responses": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/clear/group/{id}/trigger": {
      "get": {
        "operationId": "clearGroupTrigger",
        "tags": [
          "Clear"
        ],
        "summary": "Triggers the specified clear group.",
        "parameters": [
          {
            "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/parameters/0"
          }
        ],
        "responses": {
          "204": {
            "$ref": "#/paths/~1v1~1look~1current/put/responses/204"
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      }
    },
    "/v1/clear/groups": {
      "get": {
        "operationId": "clearGroupsGet",
        "tags": [
          "Clear"
        ],
        "summary": "Requests a list of all the configured clear groups.",
        "responses": {
          "200": {
            "description": "The request was processed successfully.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/paths/~1v1~1clear~1groups/post/requestBody/content/application~1json/schema"
                  }
                }
              }
            }
          },
          "404": {
            "$ref": "#/paths/~1version/get/responses/404"
          }
        }
      },
      "post": {
        "operationId": "clearGroupCreate",
        "tags": [
          "Clear"
        ],
        "summary": "Create a clear group with the details specified.",
        "description": "The UUID and index in the request body are ignored. They will be automatically\nassigned and returned in the response body.\n",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "index",
                  "name",
                  "uuid",
                  "layers",
                  "icon",
                  "tint",
                  "stop_timeline_announcements",
                  "stop_timeline_presentation"
                ],
                "properties": {
                  "id": {
                    "$ref": "#/paths/~1v1~1playlist~1current/get/responses/200/content/application~1json/schema"
                  },
                  "icon": {
                    "type": "string",
                    "format": "base64",
                    "description": "Icon to show for this clear group. Base64-encoded JPEG image data."
                  },
                  "tint": {
                    "description": "The color tint to apply to the icon for this clear group.",
                    "$ref": "#/paths/~1v1~1macro~1%7Bid%7D/put/requestBody/content/application~1json/schema/properties/color"
                  },
                  "layers": {
                    "type": "array",
                    "description": "The list of all the layers cleared by this clear group",
                    "items": {
                      "type": "string",
                      "enum": [
                        "music",
                        "audio_effects",
                        "props",
                        "messages",
                        "announcements",
                        "presentation",
                        "presentation_media",
                        "video_input"
                      ]
                    }
                  },
                  "stop_timeline_announcements": {
                    "type": "boolean",
