                    }
                },
                "description": "POST method required Payload.",
                "example": {
                    "targets": [
                        {
                            "info": {
                                "length": 11342,
                                "hashes": {
                                    "blake2b-256": "716f6e863f744b9ac22c97ec7b76ea5f5908bc5b2f67c61510bfc4751384ea7a"
