                "type": "object",
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/GetData"
                    },
                    "message": {
                        "title": "Message",
                        "type": "string"
                    }
                },
                "example": {
                    "data": {
                        "bootstrap": false
                    },
                    "message": "System available for bootstrap."
                }
            },
