            },
            "application/vnd.flux": {
              "schema": {
                "type": "string"
              },
              "example": "from(bucket: \"example-bucket\")\n    |> range(start: -5m)\n    |> filter(fn: (r) => r._measurement == \"example-measurement\")\n"
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success. The response body contains query results.",
            "headers": {
              "Content-Encoding": {
                "description": "Lists encodings (usually compression algorithms) that have been applied to the response payload.",
                "schema": {
                  "type": "string",
                  "description": "The content coding: `gzip` for compressed data or `identity` for unmodified, uncompressed data.\n",
                  "default": "identity",
                  "enum": [
                    "gzip",
                    "identity"
                  ]
                }
              },
              "Trace-Id": {
                "description": "The trace ID, if generated, of the request.",
                "schema": {
                  "type": "string",
                  "description": "Trace ID of a request."
                }
              }
            },
            "content": {
              "application/csv": {
                "schema": {
                  "type": "string"
                },
                "example": "result,table,_start,_stop,_time,region,host,_value\nmean,0,2018-05-08T20:50:00Z,2018-05-08T20:51:00Z,2018-05-08T20:50:00Z,east,A,15.43\nmean,0,2018-05-08T20:50:00Z,2018-05-08T20:51:00Z,2018-05-08T20:50:20Z,east,B,59.25\nmean,0,2018-05-08T20:50:00Z,2018-05-08T20:51:00Z,2018-05-08T20:50:40Z,east,C,52.62\n"
              }
            }
          },
          "400": {
            "description": "Bad request.\nThe response body contains detail about the error.\n\n#### InfluxDB OSS\n\n- Returns this error if `org` or `orgID` doesn't match an organization.\n",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
