{
  "components": {
    "schemas": {
      "AppId": {
        "title": "AppId",
        "type": "string"
      },
      "AppMediaObjectId": {
        "type": "string"
      },
      "AppTaskId": {
        "type": "string"
      },
      "ChannelMask": {
        "oneOf": [
          {
            "additionalProperties": false,
            "properties": {
              "mono": {
                "format": "uint",
                "minimum": 0.0,
                "type": "integer"
              }
            },
            "required": [
              "mono"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "stereo": {
                "format": "uint",
                "minimum": 0.0,
                "type": "integer"
              }
            },
            "required": [
              "stereo"
            ],
            "type": "object"
          }
        ]
      },
      "ClientId": {
        "type": "string"
      },
      "ClientSocketId": {
        "properties": {
          "client_id": {
            "$ref": "#/components/schemas/ClientId"
          },
          "socket_id": {
            "$ref": "#/components/schemas/SocketId"
          }
        },
        "required": [
          "client_id",
          "socket_id"
        ],
        "type": "object"
      },
      "CompressedAudio": {
        "properties": {
          "buffer": {
            "items": {
              "format": "uint8",
              "minimum": 0.0,
              "type": "integer"
            },
            "type": "array"
          },
          "last": {
            "type": "boolean"
          },
          "num_samples": {
            "format": "uint",
            "minimum": 0.0,
            "type": "integer"
          },
          "play_id": {
            "$ref": "#/components/schemas/PlayId"
          },
          "stream_pos": {
            "format": "uint64",
            "minimum": 0.0,
            "type": "integer"
          },
          "timeline_pos": {
            "format": "double",
            "type": "number"
          }
        },
        "required": [
          "buffer",
          "last",
          "num_samples",
          "play_id",
          "stream_pos",
          "timeline_pos"
        ],
        "type": "object"
      },
      "ConnectionValues": {
        "properties": {
          "pan": {
            "format": "double",
            "type": [
              "number",
              "null"
            ]
          },
          "volume": {
            "format": "double",
            "type": [
              "number",
              "null"
            ]
          }
        },
        "type": "object"
      },
      "CreateTask": {
        "description": "Create a task on the domain",
        "properties": {
          "reservations": {
            "allOf": [
              {
                "$ref": "#/components/schemas/CreateTaskReservation"
              }
            ],
            "description": "Task reservations"
          },
          "security": {
            "additionalProperties": {
              "$ref": "#/components/schemas/TaskPermissions"
            },
            "description": "Security keys and associateds permissions",
            "type": "object"
          },
          "spec": {
            "allOf": [
              {
                "$ref": "#/components/schemas/CreateTaskSpec"
              }
            ],
            "description": "Task specification"
          },
          "task_id": {
            "allOf": [
              {
                "$ref": "#/components/schemas/AppTaskId"
              }
            ],
            "description": "The new app id"
          }
        },
        "required": [
          "reservations",
          "security",
          "spec",
          "task_id"
        ],
        "title": "CreateTask",
        "type": "object"
      },
      "CreateTaskReservation": {
        "description": "Timed resource reservations for the task (must contain all used resources)",
        "properties": {
          "fixed_instances": {
            "description": "Fixed instances reserved for the task",
            "items": {
              "$ref": "#/components/schemas/FixedInstanceId"
            },
            "type": "array",
            "uniqueItems": true
          },
          "from": {
            "description": "Start of the reservation time",
            "format": "date-time",
            "type": "string"
          },
          "to": {
            "description": "End of the reservation time",
            "format": "date-time",
            "type": "string"
          }
        },
        "required": [
          "fixed_instances",
          "from",
          "to"
        ],
        "type": "object"
      },
      "CreateTaskSpec": {
        "description": "Create task spec",
        "properties": {
          "connections": {
            "additionalProperties": {
              "$ref": "#/components/schemas/NodeConnection"
            },
            "default": {},
            "description": "Connections between nodes",
            "type": "object"
          },
          "dynamic": {
            "additionalProperties": {
              "$ref": "#/components/schemas/DynamicInstanceNode"
            },
            "default": {},
            "description": "Dynamic instance nodes of the task",
            "type": "object"
          },
          "fixed": {
            "additionalProperties": {
              "$ref": "#/components/schemas/FixedInstanceNode"
            },
            "default": {},
            "description": "Fixed instance nodes of the task",
            "type": "object"
          },
          "mixers": {
            "additionalProperties": {
              "$ref": "#/components/schemas/MixerNode"
            },
            "default": {},
            "description": "Mixer nodes of the task",
            "type": "object"
          },
          "tracks": {
            "additionalProperties": {
              "$ref": "#/components/schemas/TrackNode"
            },
            "default": {},
            "description": "Track nodes of the task",
            "type": "object"
          }
        },
        "type": "object"
      },
      "DesiredInstancePlayState": {
        "oneOf": [
          {
            "enum": [
              "stopped"
            ],
            "type": "string"
          },
          {
            "additionalProperties": false,
            "properties": {
              "playing": {
                "properties": {
                  "play_id": {
                    "$ref": "#/components/schemas/PlayId"
                  }
                },
                "required": [
                  "play_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "playing"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "rendering": {
                "properties": {
                  "length": {
                    "format": "double",
                    "type": "number"
                  },
                  "render_id": {
                    "$ref": "#/components/schemas/RenderId"
                  }
                },
                "required": [
                  "length",
                  "render_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "rendering"
            ],
            "type": "object"
          }
        ]
      },
      "DesiredInstancePowerState": {
        "enum": [
          "powered_up",
          "shut_down"
        ],
        "type": "string"
      },
      "DesiredTaskPlayState": {
        "description": "A desired state for the task play state",
        "oneOf": [
          {
            "enum": [
              "stopped"
            ],
            "type": "string"
          },
          {
            "additionalProperties": false,
            "description": "Play, with sample rate conversion",
            "properties": {
              "play": {
                "$ref": "#/components/schemas/RequestPlay"
              }
            },
            "required": [
              "play"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "description": "Rendering is always a F32 WAV at full sample rate, so nothing else needs to happen here",
            "properties": {
              "render": {
                "$ref": "#/components/schemas/RequestRender"
              }
            },
            "required": [
              "render"
            ],
            "type": "object"
          }
        ]
      },
      "DiffStamped_for_AnyValue": {
        "description": "Difference stamped in milliseconds since a common epoch, in order to pack most efficiently The epoch in InstancePacket is the created_at field of SessionPacket",
        "items": [
          {
            "format": "uint",
            "minimum": 0.0,
            "type": "integer"
          },
          true
        ],
        "maxItems": 2,
        "minItems": 2,
        "type": "array"
      },
      "DiffStamped_for_CompressedAudio": {
        "description": "Difference stamped in milliseconds since a common epoch, in order to pack most efficiently The epoch in InstancePacket is the created_at field of SessionPacket",
        "items": [
          {
            "format": "uint",
            "minimum": 0.0,
            "type": "integer"
          },
          {
            "$ref": "#/components/schemas/CompressedAudio"
          }
        ],
        "maxItems": 2,
        "minItems": 2,
        "type": "array"
      },
      "DiffStamped_for_PadMetering": {
        "description": "Difference stamped in milliseconds since a common epoch, in order to pack most efficiently The epoch in InstancePacket is the created_at field of SessionPacket",
        "items": [
          {
            "format": "uint",
            "minimum": 0.0,
            "type": "integer"
          },
          {
            "$ref": "#/components/schemas/PadMetering"
          }
        ],
        "maxItems": 2,
        "minItems": 2,
        "type": "array"
      },
      "DomainClientMessage": {
        "description": "A message sent over a real-time communication channel to a streaming domain connection",
        "oneOf": [
          {
            "additionalProperties": false,
            "description": "Request to modify task specification",
            "properties": {
              "request_modify_task_spec": {
                "properties": {
                  "modify_spec": {
                    "description": "List of modifications to apply",
                    "items": {
                      "$ref": "#/components/schemas/ModifyTaskSpec"
                    },
                    "type": "array"
                  },
                  "optional": {
                    "description": "If true, the modifications are optional (no error if task already diverged)",
                    "type": "boolean"
                  },
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  },
                  "revision": {
                    "description": "Task version",
                    "format": "uint64",
                    "minimum": 0.0,
                    "type": "integer"
                  },
                  "task_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/AppTaskId"
                      }
                    ],
                    "description": "Id of the task to modify"
                  }
                },
                "required": [
                  "modify_spec",
                  "optional",
                  "request_id",
                  "revision",
                  "task_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "request_modify_task_spec"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "description": "Request a new WebRTC peer connection to the domain",
            "properties": {
              "request_peer_connection": {
                "properties": {
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  }
                },
                "required": [
                  "request_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "request_peer_connection"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "answer_peer_connection": {
                "properties": {
                  "answer": {
                    "description": "The domain server's WebRTC offer response (answer)",
                    "type": "string"
                  },
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  },
                  "socket_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/SocketId"
                      }
                    ],
                    "description": "The socket for which we are generating an anwser"
                  }
                },
                "required": [
                  "answer",
                  "request_id",
                  "socket_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "answer_peer_connection"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "description": "Submit a new WebRTC peer connection ICE candidate",
            "properties": {
              "submit_peer_connection_candidate": {
                "properties": {
                  "candidate": {
                    "description": "ICE Candidate",
                    "type": [
                      "string",
                      "null"
                    ]
                  },
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  },
                  "socket_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/SocketId"
                      }
                    ],
                    "description": "Socket id of the peer connection"
                  }
                },
                "required": [
                  "request_id",
                  "socket_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "submit_peer_connection_candidate"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "description": "Request attaching to a task",
            "properties": {
              "request_attach_to_task": {
                "properties": {
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  },
                  "secure_key": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/SecureKey"
                      }
                    ],
                    "description": "Secure key to use for attachment"
                  },
                  "task_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/AppTaskId"
                      }
                    ],
                    "description": "Id of the task to attach to"
                  }
                },
                "required": [
                  "request_id",
                  "secure_key",
                  "task_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "request_attach_to_task"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "request_detach_from_task": {
                "properties": {
                  "request_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/RequestId"
                      }
                    ],
                    "description": "Request id (to reference the response to)"
                  },
                  "task_id": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/AppTaskId"
                      }
                    ],
                    "description": "Id of the task to attach to"
                  }
                },
                "required": [
                  "request_id",
                  "task_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "request_detach_from_task"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "pong": {
                "properties": {
                  "challenge": {
                    "type": "string"
                  },
                  "response": {
                    "type": "string"
                  }
                },
                "required": [
                  "challenge",
                  "response"
                ],
                "type": "object"
              }
            },
            "required": [
              "pong"
            ],
            "type": "object"
          }
        ],
        "title": "DomainClientMessage"
      },
      "DomainCommand": {
        "oneOf": [
          {
            "additionalProperties": false,
            "properties": {
              "create": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  },
                  "task": {
                    "$ref": "#/components/schemas/Task"
                  }
                },
                "required": [
                  "app_session_id",
                  "task"
                ],
                "type": "object"
              }
            },
            "required": [
              "create"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "set_spec": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  },
                  "spec": {
                    "$ref": "#/components/schemas/TaskSpec"
                  },
                  "version": {
                    "format": "uint64",
                    "minimum": 0.0,
                    "type": "integer"
                  }
                },
                "required": [
                  "app_session_id",
                  "spec",
                  "version"
                ],
                "type": "object"
              }
            },
            "required": [
              "set_spec"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "set_security": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  },
                  "security": {
                    "additionalProperties": {
                      "$ref": "#/components/schemas/TaskPermissions"
                    },
                    "type": "object"
                  },
                  "version": {
                    "format": "uint64",
                    "minimum": 0.0,
                    "type": "integer"
                  }
                },
                "required": [
                  "app_session_id",
                  "security",
                  "version"
                ],
                "type": "object"
              }
            },
            "required": [
              "set_security"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "modify": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  },
                  "modifications": {
                    "items": {
                      "$ref": "#/components/schemas/ModifyTaskSpec"
                    },
                    "type": "array"
                  },
                  "version": {
                    "format": "uint64",
                    "minimum": 0.0,
                    "type": "integer"
                  }
                },
                "required": [
                  "app_session_id",
                  "modifications",
                  "version"
                ],
                "type": "object"
              }
            },
            "required": [
              "modify"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "set_desired_play_state": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  },
                  "desired_play_state": {
                    "$ref": "#/components/schemas/DesiredTaskPlayState"
                  },
                  "version": {
                    "format": "uint64",
                    "minimum": 0.0,
                    "type": "integer"
                  }
                },
                "required": [
                  "app_session_id",
                  "desired_play_state",
                  "version"
                ],
                "type": "object"
              }
            },
            "required": [
              "set_desired_play_state"
            ],
            "type": "object"
          },
          {
            "additionalProperties": false,
            "properties": {
              "delete": {
                "properties": {
                  "app_session_id": {
                    "$ref": "#/components/schemas/AppTaskId"
                  }
                },
                "required": [
                  "app_session_id"
                ],
                "type": "object"
              }
            },
            "required": [
              "delete"
            ],
            "type": "object"
          }
        ],
        "title": "DomainCommand"
      },
      "DomainError": {
        "oneOf": [
          {
            "properties": {
              "error": {
                "$ref": "#/components/schemas/InstanceDriverError"
              },
              "instance_id": {
                "$ref": "#/components/schemas/FixedInstanceId"
              },
              "type": {
                "enum": [
                  "instance_driver"
                ],
                "type": "string"
              }
            },
            "required": [
              "error",
              "instance_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "engine_id": {
                "$ref": "#/components/schemas/EngineId"
              },
              "error": {
                "$ref": "#/components/schemas/EngineError"
              },
              "type": {
                "enum": [
                  "engine"
                ],
                "type": "string"
              }
            },
            "required": [
              "engine_id",
              "error",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "engine_id": {
                "$ref": "#/components/schemas/EngineId"
              },
              "type": {
                "enum": [
                  "engine_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "engine_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "socket_id": {
                "$ref": "#/components/schemas/ClientSocketId"
              },
              "type": {
                "enum": [
                  "socket_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "socket_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "socket_id": {
                "$ref": "#/components/schemas/ClientSocketId"
              },
              "type": {
                "enum": [
                  "socket_exists"
                ],
                "type": "string"
              }
            },
            "required": [
              "socket_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "play_id": {
                "$ref": "#/components/schemas/PlayId"
              },
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_stream_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "play_id",
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "play_id": {
                "$ref": "#/components/schemas/PlayId"
              },
              "serial": {
                "format": "uint64",
                "minimum": 0.0,
                "type": "integer"
              },
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_packet_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "play_id",
              "serial",
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_exists"
                ],
                "type": "string"
              }
            },
            "required": [
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "revision": {
                "format": "uint64",
                "minimum": 0.0,
                "type": "integer"
              },
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_modification_revision_out_of_date"
                ],
                "type": "string"
              }
            },
            "required": [
              "revision",
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "error": {
                "$ref": "#/components/schemas/ModifyTaskError"
              },
              "task_id": {
                "$ref": "#/components/schemas/AppTaskId"
              },
              "type": {
                "enum": [
                  "task_modification"
                ],
                "type": "string"
              }
            },
            "required": [
              "error",
              "task_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "instance_id": {
                "$ref": "#/components/schemas/FixedInstanceId"
              },
              "type": {
                "enum": [
                  "instance_not_found"
                ],
                "type": "string"
              }
            },
            "required": [
              "instance_id",
              "type"
            ],
            "type": "object"
          },
          {
            "properties": {
              "instance_id": {
                "$ref": "#/components/schemas/FixedInstanceId"
              },
              "operation": {
