            "description": "Play id",
            "in": "path",
            "name": "play_id",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/PlayId"
            }
          },
          {
            "deprecated": false,
            "description": "Packet serial number",
            "in": "path",
            "name": "serial",
            "required": true,
            "schema": {
              "format": "int64",
              "type": "integer"
            }
          },
          {
            "deprecated": false,
            "description": "Milliseconds to wait for the packet to be ready",
            "in": "header",
            "name": "Timeout",
            "required": true,
            "schema": {
              "format": "int64",
              "type": "integer"
            }
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/StreamingPacket"
                }
              }
            },
            "description": "Success"
          },
          "401": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DomainError"
                }
              }
            },
            "description": "Not authorized"
          },
          "404": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DomainError"
                }
              }
