                        "example": "123"
                    },
                    "battery": {
                        "type": "integer",
                        "example": "14"
                    },
                    "latitude": {
                        "type": "number",
                        "example": "51.552422"
                    },
                    "longitude": {
                        "type": "number",
                        "example": "5.542853"
                    },
                    "speed": {
                        "type": "integer",
                        "example": "10"
                    }
                },
                "required": [
