                            "DIESEL",
                            "ELECTRIC",
                            "HYBRID_GASOLINE",
                            "HYBRID_DIESEL",
                            "HYBRID_GAS",
                            "HYDROGEN",
                            "GAS",
                            "BIO_MASS",
                            "KEROSINE",
