                "properties": {
                    "number": {
                        "type": "string"
                    },
                    "place": {
                        "type": "string"
                    }
                }
            },
            "legEvent": {
                "type": "object",
                "description": "event for the execution",
                "required": [
                    "time",
                    "event"
                ],
                "properties": {
                    "time": {
                        "type": "string",
                        "format": "date-time"
                    },
                    "event": {
                        "type": "string",
                        "enum": [
                            "PREPARE",
                            "ASSIGN_ASSET",
                            "SET_IN_USE",
                            "PAUSE",
                            "START_FINISHING",
                            "FINISH",
                            "TIME_EXTEND",
                            "TIME_POSTPONE"
                        ]
                    },
                    "comment": {
                        "type": "string",
                        "description": "free text, should match Content-Language"
