{
    "openapi": "3.0.0",
    "info": {
        "title": "Telegram Bot API",
        "description": "Auto-generated OpenAPI schema by TGScraper.",
        "version": "1.8.0"
    },
    "servers": [
        {
            "url": "https://api.telegram.org/bot{token}",
            "variables": {
                "token": {
                    "default": "1234:AAbbcc",
                    "description": "Bot's unique authentication token, given by @BotFather."
                }
            }
        }
    ],
    "externalDocs": {
        "description": "Official Telegram Bot API documentation.",
        "url": "https://core.telegram.org/bots/api"
    },
    "components": {
        "responses": {
            "BadRequest": {
                "description": "Bad request, you have provided malformed data.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Unauthorized": {
                "description": "The authorization token is invalid or it has been revoked.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Forbidden": {
                "description": "This action is forbidden.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "NotFound": {
                "description": "The specified resource was not found.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Conflict": {
                "description": "There is a conflict with another instance using webhook or polling.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "TooManyRequests": {
                "description": "You're doing too many requests, retry after a while.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "ServerError": {
                "description": "The bot API is experiencing some issues, try again later.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "UnknownError": {
                "description": "An unknown error occurred.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            }
        },
        "schemas": {
            "Response": {
                "type": "object",
                "description": "Represents the default response object.",
                "required": [
                    "ok"
                ],
                "properties": {
                    "ok": {
                        "type": "boolean"
                    }
                }
            },
            "Success": {
                "description": "Request was successful, the result is returned.",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/Response"
                    },
                    {
                        "type": "object",
                        "required": [
                            "result"
                        ],
                        "properties": {
                            "result": {
                                "type": "object"
                            }
                        }
                    }
                ]
            },
            "Error": {
                "description": "Request was unsuccessful, so an error occurred.",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/Response"
                    },
                    {
                        "type": "object",
                        "required": [
                            "error_code",
                            "description"
                        ],
                        "properties": {
                            "error_code": {
                                "type": "integer"
                            },
                            "description": {
                                "type": "string"
                            },
                            "parameters": {
                                "$ref": "#/components/schemas/ResponseParameters"
                            }
                        }
                    }
                ]
            },
            "Update": {
                "description": "This object represents an incoming update.Only one of the optional parameters can be present in any given update.",
                "required": [
                    "Field",
                    "update_id"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "update_id": {
                        "type": "integer"
                    },
                    "message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "inline_query": {
                        "$ref": "#/components/schemas/InlineQuery"
                    }
                },
                "type": "object"
            },
            "User": {
                "description": "This object represents a Telegram user or bot.",
                "required": [
                    "Field",
                    "id",
                    "first_name"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "id": {
                        "type": "integer"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    },
                    "username": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "Chat": {
                "description": "This object represents a chat.",
                "required": [
                    "Field",
                    "id",
                    "type"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "id": {
                        "type": "integer"
                    },
                    "type": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "username": {
                        "type": "string"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "Message": {
                "description": "This object represents a message.",
                "required": [
                    "Field",
                    "message_id",
                    "date",
                    "chat"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "message_id": {
                        "type": "integer"
                    },
                    "from": {
                        "$ref": "#/components/schemas/User"
                    },
                    "date": {
                        "type": "integer"
                    },
                    "chat": {
                        "$ref": "#/components/schemas/Chat"
                    },
                    "forward_from": {
                        "$ref": "#/components/schemas/User"
                    },
                    "forward_date": {
                        "type": "integer"
                    },
                    "reply_to_message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "text": {
                        "type": "string"
                    },
                    "audio": {
                        "$ref": "#/components/schemas/Audio"
                    },
                    "document": {
                        "$ref": "#/components/schemas/Document"
                    },
                    "photo": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PhotoSize"
                        }
                    },
                    "sticker": {
                        "$ref": "#/components/schemas/Sticker"
                    },
                    "video": {
                        "$ref": "#/components/schemas/Video"
                    },
                    "voice": {
                        "$ref": "#/components/schemas/Voice"
                    },
                    "caption": {
                        "type": "string"
                    },
                    "contact": {
                        "$ref": "#/components/schemas/Contact"
                    },
                    "location": {
                        "$ref": "#/components/schemas/Location"
                    },
                    "new_chat_participant": {
                        "$ref": "#/components/schemas/User"
                    },
                    "left_chat_participant": {
                        "$ref": "#/components/schemas/User"
                    },
                    "new_chat_title": {
                        "type": "string"
                    },
                    "new_chat_photo": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PhotoSize"
                        }
                    },
                    "delete_chat_photo": {
                        "type": "boolean",
                        "default": true
                    },
                    "group_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "supergroup_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "channel_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "migrate_to_chat_id": {
                        "type": "integer"
                    },
                    "migrate_from_chat_id": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "PhotoSize": {
                "description": "This object represents one size of a photo or a file / sticker thumbnail.",
                "required": [
                    "Field",
                    "file_id",
                    "width",
                    "height"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Audio": {
                "description": "This object represents an audio file to be treated as music by the Telegram clients.",
                "required": [
                    "Field",
                    "file_id",
                    "duration"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "performer": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Document": {
                "description": "This object represents a general file (as opposed to photos, voice messages and audio files).",
                "required": [
                    "Field",
                    "file_id"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_name": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Sticker": {
                "description": "This object represents a sticker.",
                "required": [
                    "Field",
                    "file_id",
                    "width",
                    "height"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Video": {
                "description": "This object represents a video file.",
                "required": [
                    "Field",
                    "file_id",
                    "width",
                    "height",
                    "duration"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Voice": {
                "description": "This object represents a voice note.",
                "required": [
                    "Field",
                    "file_id",
                    "duration"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Contact": {
                "description": "This object represents a phone contact.",
                "required": [
                    "Field",
                    "phone_number",
                    "first_name"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "phone_number": {
                        "type": "string"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    },
                    "user_id": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Location": {
                "description": "This object represents a point on the map.",
                "required": [
                    "Field",
                    "longitude",
                    "latitude"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "longitude": {
                        "type": "number"
                    },
                    "latitude": {
                        "type": "number"
                    }
                },
                "type": "object"
            },
            "UserProfilePhotos": {
                "description": "This object represent a user's profile pictures.",
                "required": [
                    "Field",
                    "total_count",
                    "photos"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "total_count": {
                        "type": "integer"
                    },
                    "photos": {
                        "type": "array",
                        "items": {
                            "type": "array",
                            "items": {
                                "$ref": "#/components/schemas/PhotoSize"
                            }
                        }
                    }
                },
                "type": "object"
            },
            "File": {
                "description": "This object represents a file ready to be downloaded. The file can be downloaded via the link https://api.telegram.org/file/bot<token>/<file_path>. It is guaranteed that the link will be valid for at least 1 hour. When the link expires, a new one can be requested by calling getFile.",
                "required": [
                    "Field",
                    "file_id"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "file_id": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    },
                    "file_path": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "ReplyKeyboardMarkup": {
                "description": "This object represents a custom keyboard with reply options (see Introduction to bots for details and examples).",
                "required": [
                    "Field",
                    "keyboard"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "keyboard": {
                        "type": "array",
                        "items": {
                            "type": "array",
                            "items": {
                                "type": "string"
                            }
                        }
                    },
                    "resize_keyboard": {
                        "type": "boolean"
                    },
                    "one_time_keyboard": {
                        "type": "boolean"
                    },
                    "selective": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "ReplyKeyboardHide": {
                "description": "Upon receiving a message with this object, Telegram clients will hide the current custom keyboard and display the default letter-keyboard. By default, custom keyboards are displayed until a new keyboard is sent by a bot. An exception is made for one-time keyboards that are hidden immediately after the user presses a button (see ReplyKeyboardMarkup).",
                "required": [
                    "Field",
                    "hide_keyboard"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "hide_keyboard": {
                        "type": "boolean",
                        "default": true
                    },
                    "selective": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "ForceReply": {
                "description": "Upon receiving a message with this object, Telegram clients will display a reply interface to the user (act as if the user has selected the bot‘s message and tapped ’Reply'). This can be extremely useful if you want to create user-friendly step-by-step interfaces without having to sacrifice privacy mode.",
                "required": [
                    "Field",
                    "force_reply"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "force_reply": {
                        "type": "boolean",
                        "default": true
                    },
                    "selective": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "InputFile": {
                "description": "This object represents the contents of a file to be uploaded. Must be posted using multipart/form-data in the usual way that files are uploaded via the browser.",
                "type": "object"
            },
            "InlineQuery": {
                "description": "This object represents an incoming inline query. When the user sends an empty query, your bot could return some default or trending results.",
                "required": [
                    "Field",
                    "id",
                    "from",
                    "query",
                    "offset"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "id": {
                        "type": "string"
                    },
                    "from": {
                        "$ref": "#/components/schemas/User"
                    },
                    "query": {
                        "type": "string"
                    },
                    "offset": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "InlineQueryResult": {
                "description": "This object represents one result of an inline query. Telegram clients currently support results of the following 5 types:",
                "anyOf": [
                    {
                        "$ref": "#/components/schemas/InlineQueryResultArticle"
                    },
                    {
                        "$ref": "#/components/schemas/InlineQueryResultPhoto"
                    },
                    {
                        "$ref": "#/components/schemas/InlineQueryResultGif"
                    },
                    {
                        "$ref": "#/components/schemas/InlineQueryResultMpeg4Gif"
                    },
                    {
                        "$ref": "#/components/schemas/InlineQueryResultVideo"
                    }
                ]
            },
            "InlineQueryResultArticle": {
                "description": "Represents a link to an article or web page.",
                "required": [
                    "Field",
                    "type",
                    "id",
                    "title",
                    "message_text"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "type": {
                        "type": "string",
                        "default": "article"
                    },
                    "id": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "message_text": {
                        "type": "string"
                    },
                    "parse_mode": {
                        "type": "string"
                    },
                    "disable_web_page_preview": {
                        "type": "boolean"
                    },
                    "url": {
                        "type": "string"
                    },
                    "hide_url": {
                        "type": "boolean"
                    },
                    "description": {
                        "type": "string"
                    },
                    "thumb_url": {
                        "type": "string"
                    },
                    "thumb_width": {
                        "type": "integer"
                    },
                    "thumb_height": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "InlineQueryResultPhoto": {
                "description": "Represents a link to a photo. By default, this photo will be sent by the user with optional caption. Alternatively, you can provide message_text to send it instead of photo.",
                "required": [
                    "Field",
                    "type",
                    "id",
                    "photo_url",
                    "thumb_url"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "type": {
                        "type": "string",
                        "default": "photo"
                    },
                    "id": {
                        "type": "string"
                    },
                    "photo_url": {
                        "type": "string"
                    },
                    "photo_width": {
                        "type": "integer"
                    },
                    "photo_height": {
                        "type": "integer"
                    },
                    "thumb_url": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "description": {
                        "type": "string"
                    },
                    "caption": {
                        "type": "string"
                    },
                    "message_text": {
                        "type": "string"
                    },
                    "parse_mode": {
                        "type": "string"
                    },
                    "disable_web_page_preview": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "InlineQueryResultGif": {
                "description": "Represents a link to an animated GIF file. By default, this animated GIF file will be sent by the user with optional caption. Alternatively, you can provide message_text to send it instead of the animation.",
                "required": [
                    "Field",
                    "type",
                    "id",
                    "gif_url",
                    "thumb_url"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "type": {
                        "type": "string",
                        "default": "gif"
                    },
                    "id": {
                        "type": "string"
                    },
                    "gif_url": {
                        "type": "string"
                    },
                    "gif_width": {
                        "type": "integer"
                    },
                    "gif_height": {
                        "type": "integer"
                    },
                    "thumb_url": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "caption": {
                        "type": "string"
                    },
                    "message_text": {
                        "type": "string"
                    },
                    "parse_mode": {
                        "type": "string"
                    },
                    "disable_web_page_preview": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "InlineQueryResultMpeg4Gif": {
                "description": "Represents a link to a video animation (H.264/MPEG-4 AVC video without sound). By default, this animated MPEG-4 file will be sent by the user with optional caption. Alternatively, you can provide message_text to send it instead of the animation.",
                "required": [
                    "Field",
                    "type",
                    "id",
                    "mpeg4_url",
                    "thumb_url"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "type": {
                        "type": "string",
                        "default": "mpeg4_gif"
                    },
                    "id": {
                        "type": "string"
                    },
                    "mpeg4_url": {
                        "type": "string"
                    },
                    "mpeg4_width": {
                        "type": "integer"
                    },
                    "mpeg4_height": {
                        "type": "integer"
                    },
                    "thumb_url": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "caption": {
                        "type": "string"
                    },
                    "message_text": {
                        "type": "string"
                    },
                    "parse_mode": {
                        "type": "string"
                    },
                    "disable_web_page_preview": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "InlineQueryResultVideo": {
                "description": "Represents link to a page containing an embedded video player or a video file.",
                "required": [
                    "Field",
                    "type",
                    "id",
                    "video_url",
                    "mime_type",
                    "message_text",
                    "thumb_url",
                    "title"
                ],
                "properties": {
                    "Field": {
                        "$ref": "#/components/schemas/Type"
                    },
                    "type": {
                        "type": "string",
                        "default": "video"
                    },
                    "id": {
                        "type": "string"
                    },
                    "video_url": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "message_text": {
                        "type": "string"
                    },
                    "parse_mode": {
                        "type": "string"
                    },
                    "disable_web_page_preview": {
                        "type": "boolean"
                    },
                    "video_width": {
                        "type": "integer"
                    },
                    "video_height": {
                        "type": "integer"
                    },
                    "video_duration": {
                        "type": "integer"
                    },
                    "thumb_url": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "description": {
                        "type": "string"
                    }
                },
                "type": "object"
            }
        }
    },
    "paths": {
        "/getUpdates": {
            "description": "Use this method to receive incoming updates using long polling (wiki). An Array of Update objects is returned.",
            "post": {
                "requestBody": {
                    "content": {
