                              "title": "Access list entry",
                              "type": "object",
                              "properties": {
                                "address": {
                                  "title": "hex encoded address",
                                  "type": "string",
                                  "pattern": "^0x[0-9,a-f,A-F]{40}$"
                                },
                                "storageKeys": {
                                  "type": "array",
                                  "items": {
