{
  "openapi": "3.0.0",
  "info": {
    "version": "0.0.0",
    "title": "invasivesbc-api",
    "description": "API for InvasivesBC",
    "license": {
      "name": "Apache 2.0",
      "url": "http://www.apache.org/licenses/LICENSE-2.0.html"
    }
  },
  "servers": [
    {
      "url": "http://localhost:3002/api",
      "description": "local api"
    },
    {
      "url": "http://localhost:7080/api",
      "description": "local api via docker"
    },
    {
      "url": "https://api-dev-invasivesbci.apps.silver.devops.gov.bc.ca",
      "description": "deployed api in dev environment"
    },
    {
      "url": "https://api-test-invasivesbci.apps.silver.devops.gov.bc.ca",
      "description": "deployed api in test environment"
    },
    {
      "url": "https://api-invasivesbci.apps.silver.devops.gov.bc.ca",
      "description": "deployed api in prod environment"
    }
  ],
  "tags": [
    {
      "name": "misc",
      "description": "Miscellaneous endpoints"
    },
    {
      "name": "activity",
      "description": "Activity endpointsAn activity is a parent classification that contains Observations, Treatments, and Monitoring 'activities'"
    },
    {
      "name": "observation",
      "description": "Observation activity endpoints"
    },
    {
      "name": "plant",
      "description": "Plant endpoints"
    },
    {
      "name": "code",
      "description": "Code endpoints"
    },
    {
      "name": "media",
      "description": "Media endpoints"
    }
  ],
  "externalDocs": {
    "description": "Visit GitHub to find out more about this API",
    "url": "https://github.com/bcgov/lucy-web.git"
  },
  "paths": {},
  "components": {
    "securitySchemes": {
      "Bearer": {
        "type": "http",
        "scheme": "bearer",
        "bearerFormat": "JWT",
        "description": "To access the authenticated api routes, a valid JWT token must be present in the 'Authorization' header.The 'Authorization' header value must be of the form: `Bearer xxxxxx.yyyyyyy.zzzzzz`"
      }
    },
    "responses": {
      "201": {
        "description": "Created"
      },
      "400": {
        "description": "Bad request",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            }
          }
        }
      },
      "401": {
        "description": "Unauthorized user",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            }
          }
        }
      },
      "409": {
        "description": "Conflict",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            }
          }
        }
      },
      "503": {
        "description": "Service unavailable",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            }
          }
        }
      },
      "default": {
        "description": "Unexpected error",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            }
          }
        }
      }
    },
    "schemas": {
      "Error": {
        "description": "Error response object",
        "properties": {
          "status": {
            "type": "number"
          },
          "message": {
            "type": "string"
          },
          "errors": {
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        }
      },
      "Activity_FREP_FormA": {
        "type": "object",
        "properties": {
          "activity_subtype_data": {
            "$ref": "#/components/schemas/FREP_FormA"
          }
        }
      },
      "Activity_FREP_FormB": {
        "type": "object",
        "properties": {
          "activity_subtype_data": {
            "$ref": "#/components/schemas/FREP_FormB"
          }
        }
      },
      "Activity_FREP_FormC": {
