        "operationId": "get-transaction",
        "description": "Retrieves all transactions for the business. This endpoint supports filtering and ordering. NOTE: only one ordering param is supported.",
        "parameters": [
          {
            "name": "Authorization",
            "in": "header",
            "schema": {
              "type": "string",
              "example": "Bearer x1x1x1x1x1x1x1x1x1x1"
            },
            "description": "The OAuth2 token header",
            "required": true
          },
          {
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "in": "query",
            "name": "department_id"
          },
          {
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "in": "query",
            "name": "location_id"
          },
          {
            "schema": {
              "type": "string",
              "format": "date-time"
            },
            "in": "query",
            "name": "from_date"
          },
          {
            "schema": {
              "type": "string",
              "format": "date-time"
            },
            "in": "query",
            "name": "to_date"
          },
          {
            "schema": {
              "type": "string"
            },
            "in": "query",
            "name": "merchant_id"
          },
          {
            "schema": {
              "type": "string"
            },
            "in": "query",
            "name": "sk_category_id"
          },
          {
            "schema": {
              "type": "boolean"
            },
            "in": "query",
            "name": "order_by_date_desc"
          },
          {
            "schema": {
              "type": "boolean"
            },
            "in": "query",
            "name": "order_by_date_asc"
          },
          {
            "schema": {
              "type": "boolean"
            },
            "in": "query",
            "name": "order_by_amount_desc"
          },
          {
            "schema": {
              "type": "boolean"
            },
            "in": "query",
            "name": "order_by_amount_asc"
          },
          {
            "schema": {
              "type": "string"
            },
            "in": "query",
            "name": "state"
          },
          {
            "schema": {
              "type": "number"
            },
            "in": "query",
            "name": "min_amount"
          },
          {
            "schema": {
              "type": "number"
            },
            "in": "query",
            "name": "max_amount"
          },
          {
            "name": "start",
            "in": "query",
            "required": false,
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "description": "The ID of the last entity of the previous page, used for pagination to get the next page."
          },
          {
            "name": "page_size",
            "in": "query",
            "required": false,
            "schema": {
              "type": "number"
            },
            "description": "The number of results to be returned in each page. The value must be between 2 and 10,000. If not specified, the default will be 1,000."
          },
          {
            "schema": {
              "type": "boolean"
            },
            "in": "query",
            "name": "requires_memo",
            "description": "Filters for transactions which require a memo, but do not have one. This can only be set to true."
          }
        ],
        "security": [
          {
            "OAuth 2.0": [
              "transactions:read"
            ]
          }
        ]
      },
      "parameters": []
    },
