    "/higher_level_reviews/schema": {
      "get": {
        "summary": "Gets the Higher-Level Review JSON Schema.",
        "tags": [
          "Higher-Level Reviews"
        ],
        "operationId": "hlrSchema",
        "description": "Returns the [JSON Schema](https://json-schema.org/) for the `POST /higher_level_reviews` endpoint.",
        "security": [
          {
            "apikey": [

            ]
          }
        ],
        "responses": {
          "200": {
            "description": "the JSON Schema for POST /higher_level_reviews",
            "content": {
              "application/json": {
                "example": {
                  "$schema": "http://json-schema.org/draft-07/schema#",
                  "description": "JSON Schema for VA Form 20-0996",
                  "$ref": "#/definitions/hlrCreate",
                  "definitions": {
                    "nonBlankString": {
                      "type": "string",
                      "pattern": "[^ \\f\\n\\r\\t\\v\\u00a0\\u1680\\u2000-\\u200a\\u2028\\u2029\\u202f\\u205f\\u3000\\ufeff]"
                    },
                    "date": {
                      "type": "string",
                      "pattern": "^[0-9]{4}-[0-9]{2}-[0-9]{2}$"
                    },
                    "hlrCreatePhone": {
                      "type": "object",
                      "properties": {
                        "countryCode": {
                          "type": "string",
                          "pattern": "^[0-9]+$",
                          "minLength": 1,
                          "maxLength": 3
                        },
                        "areaCode": {
                          "type": "string",
                          "pattern": "^[0-9]{1,4}$",
                          "minLength": 1,
                          "maxLength": 4
                        },
                        "phoneNumber": {
                          "type": "string",
                          "pattern": "^[0-9]{1,14}$",
                          "minLength": 1,
                          "maxLength": 14
                        },
                        "phoneNumberExt": {
                          "type": "string",
                          "pattern": "^[a-zA-Z0-9]{1,10}$",
                          "minLength": 1,
                          "maxLength": 10
                        }
                      },
                      "required": [
                        "areaCode",
                        "phoneNumber"
                      ]
                    },
                    "hlrCreate": {
                      "type": "object",
                      "properties": {
                        "data": {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "higherLevelReview"
                              ]
                            },
                            "attributes": {
                              "description": "If informal conference requested (`informalConference: true`), contact (`informalConferenceContact`) and time (`informalConferenceTime`) must be specified.",
                              "type": "object",
                              "additionalProperties": false,
                              "properties": {
                                "informalConference": {
                                  "type": "boolean"
                                },
                                "benefitType": {
                                  "type": "string",
                                  "enum": [
                                    "compensation",
                                    "pensionSurvivorsBenefits",
                                    "fiduciary",
                                    "lifeInsurance",
                                    "veteransHealthAdministration",
                                    "veteranReadinessAndEmployment",
                                    "loanGuaranty",
                                    "education",
                                    "nationalCemeteryAdministration"
                                  ]
                                },
                                "veteran": {
                                  "type": "object",
                                  "properties": {
                                    "homeless": {
                                      "type": "boolean"
                                    },
                                    "address": {
                                      "type": "object",
                                      "properties": {
                                        "addressLine1": {
                                          "type": "string",
                                          "maxLength": 60
