            "name": "X-VA-SSN",
            "required": true,
            "description": "Veteran's SSN",
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "header",
            "name": "X-VA-First-Name",
            "required": true,
            "description": "Veteran's first name",
