                        "type": "array",
                        "description": "'upload' indicates that the Veteran has supplied additional evidence as uploaded documents. 'retrieval' indicates that the Veteran has supplied contact information for facilities holding evidence relevant to the claim; the 'retrieveFrom' field will contain the facility's contact information.",
                        "items": {
                          "type": "string",
                          "enum": [
                            "upload",
                            "retrieval"
                          ]
                        },
                        "minItems": 1,
                        "maxItems": 2,
                        "uniqueItems": true
                      },
                      "retrieveFrom": {
                        "type": "array",
                        "items": {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "retrievalEvidence"
                              ]
                            },
                            "attributes": {
                              "type": "object",
                              "properties": {
                                "locationAndName": {
                                  "type": "string",
                                  "minLength": 1,
                                  "maxLength": 255
                                },
                                "evidenceDates": {
                                  "type": "array",
                                  "items": {
                                    "type": "object",
                                    "properties": {
                                      "startDate": {
                                        "$ref": "#/components/schemas/date"
                                      },
                                      "endDate": {
                                        "$ref": "#/components/schemas/date"
                                      }
                                    },
                                    "required": [
                                      "startDate",
                                      "endDate"
                                    ]
                                  },
                                  "minItems": 1,
                                  "maxItems": 4
                                }
                              },
                              "required": [
                                "locationAndName",
                                "evidenceDates"
                              ]
                            }
                          },
                          "required": [
                            "type",
                            "attributes"
                          ]
                        },
                        "minItems": 1,
                        "uniqueItems": true
                      }
                    },
                    "required": [
                      "evidenceType"
                    ],
                    "if": {
                      "properties": {
                        "evidenceType": {
                          "const": "retrieval"
                        }
                      }
                    },
                    "then": {
                      "required": [
                        "retrieveFrom"
                      ]
                    }
                  },
                  "form5103Acknowledged": {
                    "type": "boolean",
                    "description": "The 5103 Notice regarding new & relevant evidence must be acknowledged when the issue(s) being contested is a Disability Compensation issue. The notice can be found here: http://www.va.gov/disability/how-to-file-claim/evidence-needed. If the issue(s) being submitted are Disability Compensation and 'No' is selected, the API will return an error.  Please ensure the Veteran reviews the content about the 5103 Notice at the link above."
                  },
                  "socOptIn": {
                    "type": "boolean"
                  }
                },
                "allOf": [
                  {
                    "required": [
                      "veteran",
                      "benefitType",
                      "claimantType",
                      "evidenceSubmission",
                      "socOptIn"
                    ]
                  },
                  {
                    "if": {
                      "properties": {
                        "benefitType": {
                          "const": "compensation"
                        }
                      }
                    },
                    "then": {
                      "required": [
                        "form5103Acknowledged"
                      ],
                      "properties": {
                        "form5103Acknowledged": {
                          "enum": [
                            true
                          ]
                        }
                      }
                    }
                  },
                  {
                    "if": {
                      "properties": {
                        "claimantType": {
                          "const": "other"
                        }
                      }
                    },
                    "then": {
                      "required": [
                        "claimantTypeOtherValue"
                      ]
                    }
                  },
                  {
                    "if": {
                      "properties": {
                        "claimantType": {
                          "enum": [
                            "spouse_of_veteran",
                            "child_of_veteran",
                            "parent_of_veteran",
                            "other"
                          ]
                        }
                      }
                    },
                    "then": {
                      "required": [
                        "claimant"
                      ]
                    }
                  }
                ]
              }
            },
            "required": [
              "type",
              "attributes"
            ]
          },
          "included": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "type": {
                  "type": "string",
                  "enum": [
                    "contestableIssue"
                  ]
                },
                "attributes": {
                  "type": "object",
                  "properties": {
                    "issue": {
                      "type": "string",
                      "minLength": 1,
                      "maxLength": 140
                    },
                    "decisionDate": {
                      "$ref": "#/components/schemas/date"
                    },
                    "decisionIssueId": {
                      "type": "integer"
                    },
                    "ratingIssueReferenceId": {
                      "type": "string",
                      "minLength": 1,
                      "maxLength": 255
                    },
                    "ratingDecisionReferenceId": {
                      "type": "string",
                      "minLength": 1,
                      "maxLength": 255
                    },
                    "socDate": {
                      "$ref": "#/components/schemas/date"
                    }
                  },
                  "required": [
                    "issue",
                    "decisionDate"
                  ]
                }
              },
              "required": [
                "type",
                "attributes"
              ]
            },
            "minItems": 1,
            "maxItems": 100,
            "uniqueItems": true
          }
        },
        "required": [
          "data",
          "included"
        ]
      },
      "scCreateResponse": {
        "description": "Successful response of a 200995 form submission",
        "type": "object",
        "properties": {
          "data": {
            "properties": {
