{
  "openapi": "3.0.1",
  "info": {
    "title": "Winding Tree aggregator",
    "description": "This is the interface documentation for the REST api interface with the Winding Tree Aggregator",
    "contact": {
      "email": "mathieu@windingtree.com"
    },
    "license": {
      "name": "Apache License",
      "url": "http://www.apache.org/licenses/LICENSE-2.0.html"
    },
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "/api/v1",
      "description": "Current server"
    }
  ],
  "tags": [
    {
      "name": "offers",
      "description": "Operations with Offers"
    },
    {
      "name": "orders",
      "description": "Operations with Orders"
    }
  ],
  "paths": {
    "/offers/search": {
      "post": {
        "tags": [
          "offers"
        ],
        "summary": "Search for offers matching search criterias",
        "operationId": "offersWithSearchCriteria",
        "requestBody": {
          "description": "Search for offers matching input criteria",
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/SearchCriteria"
              },
              "examples": {
                "flightSearch": {
                  "summary": "A search for flights",
                  "value": {
                    "itinerary": {
                      "segments": [
                        {
                          "origin": {
                            "locationType": "airport",
                            "iataCode": "CDG"
                          },
                          "destination": {
                            "locationType": "airport",
                            "iataCode": "PRG"
                          },
                          "departureTime": "2020-04-14T00:00:00Z"
                        }
                      ]
                    },
                    "passengers": [
                      {
                        "type": "ADT",
                        "count": 2
                      },
                      {
                        "type": "CHD"
                      }
                    ]
                  }
                },
                "hotelSearchByRectangle": {
                  "summary": "A search for hotels by rectangle coordinates",
                  "value": {
                    "accommodation": {
                      "location": {
                        "rectangle": {
                          "south": "50.0929802",
                          "west": "14.4012451",
                          "north": "50.0812615",
                          "east": "14.4394467"
                        }
                      },
                      "arrival": "2020-07-02T00:00:00Z",
                      "departure": "2020-07-03T00:00:00Z"
                    },
                    "passengers": [
                      {
                        "type": "ADT",
                        "count": 2
                      },
                      {
                        "type": "CHD",
                        "count": 1
                      }
                    ]
                  }
                },
                "hotelSearchByCircle": {
                  "summary": "A search for hotels by circle",
                  "value": {
                    "accommodation": {
                      "location": {
                        "circle": {
                          "longitude": 18.05437602,
                          "latitude": 59.33309777,
                          "radius": 50
                        }
                      },
                      "arrival": "2020-07-02T00:00:00Z",
                      "departure": "2020-07-03T00:00:00Z"
                    },
                    "passengers": [
                      {
                        "type": "ADT",
                        "count": 2
                      },
                      {
                        "type": "CHD",
                        "count": 1
                      }
                    ]
                  }
                },
                "hotelSearchByPolygon": {
                  "summary": "A search for hotels by polygon",
                  "description": "For getting of polygon coordinates for testing purposes can be used online visual tool [https://www.keene.edu/campus/maps/tool/](https://www.keene.edu/campus/maps/tool/)\n",
                  "value": {
                    "accommodation": {
                      "location": {
                        "polygon": [
                          [
                            24.8963928,
                            60.1749466
                          ],
                          [
                            24.9700356,
                            60.1763126
                          ],
                          [
                            24.9720097,
                            60.1475721
                          ],
                          [
                            24.8987961,
                            60.1442394
                          ],
                          [
                            24.896307,
                            60.1748612
                          ]
                        ]
                      },
                      "arrival": "2020-07-02T00:00:00Z",
                      "departure": "2020-07-03T00:00:00Z"
                    },
                    "passengers": [
                      {
                        "type": "ADT",
                        "count": 2
                      },
                      {
                        "type": "CHD",
                        "count": 1
                      }
                    ]
                  }
                }
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Offers Retrieved",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/SearchResults"
                }
              }
            }
          },
          "401": {
            "description": "Authorization required",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "405": {
            "description": "Validation exception",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "415": {
            "description": "Content should be application/json",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "429": {
            "description": "Too many requests",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "500": {
            "description": "Internal Server error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "security": [
          {
            "orgid_auth": [
              "read:orders"
            ]
          },
          {
            "orgid_token": []
          }
        ],
        "x-codegen-request-body-name": "body"
      }
    },
    "/offers/{offerIds}/price": {
      "post": {
        "tags": [
          "offers"
        ],
        "summary": "Get the final price for a list of offers",
        "description": "Providing an offer information with confirmed price",
        "operationId": "priceOffer",
        "parameters": [
          {
            "name": "offerIds",
            "in": "path",
            "description": "IDs of offers separated by a comma",
            "required": true,
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/UUID"
              }
            }
          }
        ],
        "requestBody": {
          "description": "Optionally provide the selected options",
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/OptionSelectionList"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Offer with confirmed price",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/PricedOffer"
                }
              }
            }
          },
          "400": {
            "description": "Invalid ID supplied",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "401": {
            "description": "Authorization required",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to retrieve this order",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "404": {
            "description": "Order not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "415": {
            "description": "Content should be application/json",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "429": {
            "description": "Too many requests, rate limit has been reached",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "security": [
          {
            "orgid_auth": [
              "read:offers"
            ]
          },
          {
            "orgid_token": []
          }
        ]
      }
    },
    "/offers/{offerIds}/seatmap": {
      "get": {
        "tags": [
          "offers"
        ],
        "summary": "Retrieve the seats available to select",
        "description": "Use this operation to retrieve the seatmap in a flight offer. The service provides information on the layout of the cabin and the seats available",
        "operationId": "getSeatmap",
        "parameters": [
          {
            "name": "offerIds",
            "in": "path",
            "description": "ID of the offers",
            "required": true,
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/UUID"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "List of seatmap indexed by offerId",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "seatmaps": {
                      "description": "Contains the list of seatmaps indexed by segment",
                      "additionalProperties": {
                        "$ref": "#/components/schemas/SeatMap"
                      }
                    },
                    "prices": {
                      "description": "Contains the list of seat prices indexed by option code",
                      "additionalProperties": {
                        "$ref": "#/components/schemas/Price"
                      }
                    }
                  }
                }
              }
            }
          },
          "400": {
            "description": "Invalid Offer Ids supplied",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "401": {
            "description": "Authorization required",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to access this offer",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "404": {
            "description": "Offer(s) not found or no service to offer",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "415": {
            "description": "Content should be application/json",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "429": {
            "description": "Too many requests, rate limit has been reached",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "security": [
          {
            "orgid_auth": [
              "read:offers"
            ]
          },
          {
            "orgid_token": []
          }
        ]
      }
    },
    "/orders/createWithOffer": {
      "post": {
        "tags": [
          "orders"
        ],
        "summary": "Place an order using a given offer",
        "operationId": "orderCreateWithOffer",
        "requestBody": {
          "description": "Reference of the offers and details of the passengers",
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/CreateOfferBody"
              },
              "example": {
                "offerId": "11111111-2222-3333-4444-000000000001",
                "guaranteeId": "02a1a7c0-3ff8-4e12-a3ba-65d57e1e9276",
                "passengers": {
                  "PAX1": {
                    "type": "ADT",
                    "civility": "MR",
                    "lastnames": [
                      "Marley"
                    ],
                    "firstnames": [
                      "Bob"
                    ],
                    "gender": "Male",
                    "birthdate": "1980-03-21T00:00:00Z",
                    "contactInformation": [
                      "+32123456789",
                      "contact@org.co.uk"
                    ]
                  }
                }
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Order Created",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "orderId": {
                      "description": "The ID of the order Created",
                      "$ref": "#/components/schemas/UUID"
                    },
                    "order": {
                      "description": "The created order",
                      "$ref": "#/components/schemas/Order"
                    }
                  }
                }
              }
            }
          },
          "401": {
            "description": "Authorization required",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "405": {
            "description": "Validation exception",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "415": {
            "description": "Content should be application/json",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "429": {
            "description": "Too many requests",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "500": {
            "description": "Internal Server error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "security": [
          {
            "orgid_auth": [
              "write:orders"
            ]
          },
          {
            "orgid_token": []
          }
        ],
        "x-codegen-request-body-name": "body"
      }
    },
    "/orders/{orderId}/fulfill": {
      "post": {
        "tags": [
          "orders"
        ],
        "summary": "Fulfills an order in Aggregator",
        "description": "Confirms an order, providing travel documents if relevant",
        "operationId": "fulfillOrder",
        "parameters": [
          {
            "name": "orderId",
            "in": "path",
            "description": "ID of the order to sign",
            "required": true,
            "schema": {
              "oneOf": [
                {
                  "$ref": "#/components/schemas/UUID"
                },
                {
                  "$ref": "#/components/schemas/ShortOrderId"
                }
              ]
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "properties": {
                  "orderItems": {
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/UUID"
                    }
                  },
                  "passengerReferences": {
                    "type": "array",
                    "items": {
                      "description": "The identifier of a passenger",
                      "type": "string",
                      "example": "PAX1"
                    }
                  },
                  "guaranteeId": {
                    "$ref": "#/components/schemas/UUID"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Order sucessfully signed",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "orderId": {
                      "$ref": "#/components/schemas/UUID"
                    },
                    "order": {
                      "$ref": "#/components/schemas/Order"
                    },
                    "travelDocuments": {
                      "description": "The travel documents provided by the supplier",
                      "type": "object",
                      "properties": {
                        "bookings": {
                          "description": "The booking references",
                          "type": "array",
                          "items": {
                            "type": "string",
                            "pattern": "^[A-Z0-9]{6}$",
                            "example": "LCHTB4"
                          }
                        },
                        "etickets": {
                          "description": "The electronic ticket references",
                          "type": "array",
                          "items": {
                            "type": "string",
                            "pattern": "^[0-9]{3}-[0-9]{10}$",
                            "example": "057-1438027611"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "400": {
            "description": "Invalid ID supplied",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "401": {
            "description": "Authorization required",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to retrieve this order",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "404": {
            "description": "Order not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "415": {
            "description": "Content should be application/json",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "429": {
            "description": "Too many requests, rate limit has been reached",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "security": [
          {
            "orgid_auth": [
              "write:orders"
            ]
          },
          {
            "orgid_token": []
          }
        ]
      }
    }
  },
  "components": {
    "schemas": {
      "Error": {
        "description": "Objet describing an error",
        "type": "object",
        "properties": {
          "message": {
            "description": "Error message",
            "type": "string",
            "example": "Something went wrong"
          },
          "code": {
            "description": "Error code",
            "type": "string",
            "pattern": "^[0-9A-Z]+$",
            "example": "E01"
          }
        }
      },
      "UUID": {
        "description": "Unique identifier for Offers and Orders",
        "type": "string",
        "format": "uuid",
        "minLength": 36,
        "maxLength": 36,
        "pattern": "^[0-9a-f]{8}-([0-9a-f]{4}-){3}[0-9a-f]{12}$",
        "example": "11111111-2222-3333-4444-000000000001"
      },
      "ShortOrderId": {
        "description": "Unique short order Id",
        "type": "string",
        "minLength": 6,
        "maxLength": 6,
        "pattern": "^[0-9a-zA-Z]{6}$",
        "example": "PUVYL9"
      },
      "OrgId": {
        "description": "ORG.ID identifier",
        "type": "string",
        "pattern": "^0x[a-fA-F0-9]{64}$",
        "minLength": 64,
        "maxLength": 64,
        "example": "0xc0fbc1348b43d50c948edf1818b0abfdd7466b9e"
      },
      "Amount": {
        "description": "An amount",
        "type": "string",
        "pattern": "^[0-9]+(\\.[0-9]+){0,1}$",
        "minLength": 1,
        "maxLength": 99,
        "example": "40.00"
      },
      "Currency": {
        "description": "The ISO4217 currency code",
        "type": "string",
        "pattern": "^[A-Z]{3}$",
        "minLength": 3,
        "maxLength": 3,
        "example": "EUR"
      },
      "Price": {
        "description": "Price of the service",
        "type": "object",
        "properties": {
          "currency": {
            "$ref": "#/components/schemas/Currency"
          },
          "private": {
            "$ref": "#/components/schemas/Amount"
          },
          "public": {
            "$ref": "#/components/schemas/Amount"
          },
          "commission": {
            "$ref": "#/components/schemas/Amount"
          },
          "taxes": {
            "$ref": "#/components/schemas/Amount"
          }
        }
      },
      "ServiceRestrictions": {
        "description": "Service restrictions",
        "type": "object",
        "properties": {
          "refundable": {
            "description": "Is the service refundable?",
            "type": "boolean",
            "example": false
          },
          "exchangeable": {
            "description": "Is the service exchangeable?",
            "type": "boolean",
            "example": false
          },
          "refundFee": {
            "$ref": "#/components/schemas/Amount"
          },
          "exchangeFee": {
            "$ref": "#/components/schemas/Amount"
          }
        }
      },
      "Email": {
        "description": "An email",
        "type": "string",
        "pattern": "^.+?@.+\\..+?$",
        "example": "contact@org.co.uk"
      },
      "PhoneNumber": {
        "description": "A phone number",
        "type": "string",
        "pattern": "^\\+[0-9]+$",
        "example": "+32123456789"
      },
      "Name": {
        "description": "First, middle or last name",
        "type": "string",
        "minLength": 1,
        "maxLength": 42,
        "example": "Bob"
      },
      "Passenger": {
        "description": "A representation of a passenger",
        "type": "object",
        "required": [
          "type"
        ],
        "properties": {
          "type": {
            "description": "The passenger type code as per IATA definitions",
            "type": "string",
            "minLength": 3,
            "maxLength": 3,
            "enum": [
              "ADT",
              "CHD",
              "INF"
            ],
            "example": "ADT"
          },
          "count": {
            "description": "Number of associated passengers of the same type",
            "type": "integer",
            "example": 2,
            "default": 1
          },
          "civility": {
            "description": "The civility of the passenger",
            "type": "string",
            "enum": [
              "MR",
              "MRS"
            ],
            "example": "MR"
          },
          "lastnames": {
            "description": "The list of last names of this passenger",
            "type": "array",
            "minItems": 1,
            "maxItems": 5,
            "items": {
              "$ref": "#/components/schemas/Name"
            }
          },
          "firstnames": {
            "description": "The list of first names of this passenger",
            "type": "array",
            "minItems": 1,
            "maxItems": 5,
            "items": {
              "$ref": "#/components/schemas/Name"
            }
          },
          "middlenames": {
            "description": "The list of middle names of this passenger",
            "type": "array",
            "minItems": 1,
            "maxItems": 5,
            "items": {
              "$ref": "#/components/schemas/Name"
            }
          },
          "gender": {
            "description": "The gender of the passenger",
            "type": "string",
            "enum": [
              "Male",
              "Female"
            ],
            "example": "Male"
          },
          "birthdate": {
            "description": "The birthdate of the passenger",
            "type": "string",
            "format": "date",
            "example": "1980-03-21"
          },
          "contactInformation": {
            "description": "The contact informations of the passenger",
            "type": "array",
            "minItems": 1,
            "items": {
              "oneOf": [
                {
                  "$ref": "#/components/schemas/PhoneNumber"
                },
                {
                  "$ref": "#/components/schemas/Email"
                }
              ]
