    "/uploads/batch-presign-multipart-parts.json": {
      "post": {
        "summary": "Generates batches of presigned URLs for multipart parts",
        "tags": [
          "Uploads"
        ],
        "operationId": "batchPresignMultipartParts",
        "description": "Multipart uploads are uploaded in chunks or parts to individual presigned\nURLs, similar to the one genreated by /generate-presigned-put. The part\nnumbers provided must be between 1 and 10000. The total number of parts\nwill depend on the chunk size in bytes that you intend to use to upload\neach chunk. For example a 12MB file may have 2 5MB chunks and a final\n2MB chunk, for part numbers 1, 2, and 3.\n\nThis endpoint will return a presigned URL for each part number provided,\nwhich you can then use to send PUT requests for the binary chunk corresponding\nto that part. When the part is uploaded, the provider should return an\nETag for the part, and this should be stored along with the part number,\nbecause this is needed to complete the multipart upload.\n\nYou must have the correct permissions and CORS settings configured in your\nexternal provider. We support AWS S3 as the default. See:\n\nhttps://meta.discourse.org/t/-/210469#s3-multipart-direct-uploads-4.\n\nAn external file store must be set up and `enable_direct_s3_uploads` must\nbe set to true for this endpoint to function.\n\n",
        "parameters": [],
        "responses": {
          "200": {
            "description": "external upload initialized",
            "content": {
              "application/json": {
                "schema": {
                  "additionalProperties": false,
                  "properties": {
                    "presigned_urls": {
                      "type": "object",
                      "description": "The presigned URLs for each part number, which has\nthe part numbers as keys.",
                      "example": {
                        "1": "https://discourse-martin-uploads-test.s3.us-east-2.amazonaws.com/temp/uploads/default/123abc/123abc.jpg?partNumber=1&uploadId=123456abcd&X-Amz-Algorithm=AWS4-HMAC-SHA256&X-Amz-Credential=test&X-Amz-Date=20211222T012336Z&X-Amz-Expires=600&X-Amz-SignedHeaders=host&X-Amz-Signature=abc123"
                      }
                    }
                  },
                  "required": [
                    "presigned_urls"
                  ]
                }
              }
            }
          }
        },
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "additionalProperties": false,
                "properties": {
                  "part_numbers": {
                    "type": "array",
                    "description": "The part numbers to generate the presigned URLs for,\nmust be between 1 and 10000.",
                    "example": [
                      1,
                      2,
                      3
                    ]
                  },
                  "unique_identifier": {
                    "type": "string",
                    "description": "The unique identifier returned in the original /create-multipart\nrequest.",
                    "example": "66e86218-80d9-4bda-b4d5-2b6def968705"
                  }
                },
                "required": [
                  "part_numbers",
                  "unique_identifier"
                ]
              }
            }
          }
        }
      }
    },
