{
    "openapi": "3.0.0",
    "info": [],
    "paths": {
        "/language": {
            "post": {
                "tags": [
                    "System & Context",
                    "Endpoints supporting Criteria "
                ],
                "summary": "Fetch languages",
                "description": "Perform a filtered search for languages.",
                "operationId": "readLanguages",
                "parameters": [],
                "requestBody": {
                    "required": false,
                    "content": {
                        "application/json": {
                            "schema": {
                                "allOf": [
                                    {
                                        "$ref": "#/components/schemas/Criteria"
                                    }
                                ]
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Entity search result containing languages.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "allOf": [
                                        {
                                            "properties": {
                                                "elements": {
                                                    "type": "array",
                                                    "items": {
                                                        "$ref": "#/components/schemas/Language"
                                                    }
                                                }
                                            },
                                            "type": "object"
                                        },
                                        {
                                            "$ref": "#/components/schemas/EntitySearchResult"
                                        }
                                    ]
                                }
                            }
                        }
                    }
                },
                "security": [
                    {
                        "ApiKey": []
                    }
                ]
            }
        }
    }
}
