    "responses": {
      "AuthorizationError": {
        "description": "Unauthorized. The error may indicate one of the following:\n\n  * The `Authorization: Token` header is missing or malformed.\n  * The API token value is missing from the header.\n  * The token does not have sufficient permissions to write to this organization and bucket.\n",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "tokenNotAuthorized": {
                "summary": "Token is not authorized to access a resource",
                "value": {
                  "code": "unauthorized",
                  "message": "unauthorized access"
                }
              }
            }
          }
        }
      },
      "BadRequestError": {
        "description": "Bad request.\nThe response body contains detail about the error.\n\n#### InfluxDB OSS\n\n- Returns this error if an incorrect value is passed for `org` or `orgID`.\n",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "orgProvidedNotFound": {
                "summary": "The org or orgID passed doesn't own the token passed in the header",
                "value": {
                  "code": "invalid",
                  "message": "failed to decode request body: organization not found"
                }
              }
            }
          }
        }
      },
