{
    "openapi": "3.0.1",
    "info": {
        "title": "Service Management Public REST API",
        "description": "This is a generated connector for [Jira Service Management Cloud REST API](https://developer.atlassian.com/cloud/jira/service-desk/) OpenAPI specification. \nThe Jira Service Management Cloud REST APIs are for developers who want to integrate Jira Service Management with other applications or administrators that want to automate their workflows and processes.",
        "termsOfService": "https://www.atlassian.com/legal/customer-agreement",
        "version": "1001.0.0-SNAPSHOT",
        "x-ballerina-init-description": "The connector initialization requires setting the API credentials. \nCreate an [Atlassian Account](https://id.atlassian.com/signup) \nand obtain tokens by following [this guide](https://developer.atlassian.com/cloud/jira/platform/basic-auth-for-rest-apis/).",
        "x-ballerina-display" : {
            "label" : "Jira Service Management",
            "iconPath" : "icon.png"
        }

    },
    "tags": [
        {
            "name": "Customer"
        },
        {
            "name": "Info"
        },
        {
            "name": "Insight"
        },
        {
            "name": "Knowledgebase"
        },
        {
            "name": "Organization"
        },
        {
            "name": "Request"
        },
        {
            "name": "Requesttype"
        },
        {
            "name": "Servicedesk"
        }
    ],
    "paths": {
        "/rest/servicedeskapi/customer": {
            "post": {
                "tags": [
                    "Customer"
                ],
                "summary": "Create customer",
                "description": "This method adds a customer to the Jira Service Management instance by passing a JSON file including an email address and display name. The display name does not need to be unique. The record's identifiers, `name` and `key`, are automatically generated from the request details.\n\n**[Permissions](#permissions) required**: Jira Administrator Global permission",
                "operationId": "createCustomer",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/CustomerCreateDTO"
                            },
                            "example": {
                                "displayName": "Fred F. User",
                                "email": "fred@example.com"
                            }
                        }
                    },
                    "description": "Customer details",
                    "required": true
                },
                "responses": {
                    "201": {
                        "description": "Returns the customer details.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/UserDTO"
                                },
                                "example": "{\"accountId\":\"qm:a713c8ea-1075-4e30-9d96-891a7d181739:5ad6d3581db05e2a66fa80b\",\"name\":\"qm:a713c8ea-1075-4e30-9d96-891a7d181739:5ad6d3581db05e2a66fa80b\",\"key\":\"qm:a713c8ea-1075-4e30-9d96-891a7d181739:5ad6d3581db05e2a66fa80b\",\"emailAddress\":\"fred@example.com\",\"displayName\":\"Fred F. User\",\"active\":true,\"timeZone\":\"Australia/Sydney\",\"_links\":{\"jiraRest\":\"https://your-domain.atlassian.net/rest/api/2/user?username=qm:a713c8ea-1075-4e30-9d96-891a7d181739:5ad6d3581db05e2a66fa80b\",\"avatarUrls\":{\"48x48\":\"https://avatar-cdn.atlassian.com/9bc3b5bcb0db050c6d7660b28a5b86c9?s=48&d=https%3A%2F%2Fsecure.gravatar.com%2Favatar%2F9bc3b5bcb0db050c6d7660b28a5b86c9%3Fd%3Dmm%26s%3D48%26noRedirect%3Dtrue\",\"24x24\":\"https://avatar-cdn.atlassian.com/9bc3b5bcb0db050c6d7660b28a5b86c9?s=24&d=https%3A%2F%2Fsecure.gravatar.com%2Favatar%2F9bc3b5bcb0db050c6d7660b28a5b86c9%3Fd%3Dmm%26s%3D24%26noRedirect%3Dtrue\",\"16x16\":\"https://avatar-cdn.atlassian.com/9bc3b5bcb0db050c6d7660b28a5b86c9?s=16&d=https%3A%2F%2Fsecure.gravatar.com%2Favatar%2F9bc3b5bcb0db050c6d7660b28a5b86c9%3Fd%3Dmm%26s%3D16%26noRedirect%3Dtrue\",\"32x32\":\"https://avatar-cdn.atlassian.com/9bc3b5bcb0db050c6d7660b28a5b86c9?s=32&d=https%3A%2F%2Fsecure.gravatar.com%2Favatar%2F9bc3b5bcb0db050c6d7660b28a5b86c9%3Fd%3Dmm%26s%3D32%26noRedirect%3Dtrue\"},\"self\":\"https://your-domain.atlassian.net/rest/api/2/user?username=qm:a713c8ea-1075-4e30-9d96-891a7d181739:5ad6d3581db05e2a66fa80b\"}}"
                            }
                        }
                    },
                    "400": {
                        "description": "Returned if the request is invalid, either because the email address is incorrectly formed or already exists in the database.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "Returned if the user is not logged in.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "Returned if the user does not have permission to complete this request.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "Internal Server Error.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    }
                },
                "deprecated": false,
                "security": [
                    {
                        "OAuth2": [
                            "manage:servicedesk-customer"
                        ]
                    }
                ],
                "x-atlassian-oauth2-scopes": [
                    {
                        "state": "Current",
                        "scheme": "OAuth2",
                        "scopes": [
                            "manage:servicedesk-customer"
                        ]
                    },
                    {
                        "state": "Beta",
                        "scheme": "OAuth2",
                        "scopes": [
                            "read:customer:jira-service-management",
                            "write:customer:jira-service-management",
                            "read:user:jira"
                        ]
                    }
                ],
                "x-atlassian-connect-scope": "ADMIN"
            }
        },
        "/rest/servicedeskapi/info": {
            "get": {
                "tags": [
                    "Info"
                ],
                "summary": "Get info",
                "description": "This method retrieves information about the Jira Service Management instance such as software version, builds, and related links.\n\n**[Permissions](#permissions) required**: None, the user does not need to be logged in.",
                "operationId": "getInfo",
                "responses": {
                    "200": {
                        "description": "Returns the runtime information for the Jira Service Management instance.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/SoftwareInfoDTO"
                                },
                                "example": "{\"version\":\"3.0.1\",\"platformVersion\":\"7.0.1\",\"buildDate\":{\"iso8601\":\"2015-09-15T02:34:00+0700\",\"jira\":\"2015-09-15T02:34:00.000+0700\",\"friendly\":\"Monday 02:34 AM\",\"epochMillis\":1442259240000},\"buildChangeSet\":\"c6679417c550918e7c94a9eaaada133f15dc8ff0\",\"isLicensedForUse\":true,\"_links\":{\"self\":\"https://your-domain.atlassian.net/rest/servicedeskapi/info\"}}"
                            }
                        }
                    },
                    "500": {
                        "description": "Internal Server Error.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    }
                },
                "deprecated": false,
                "security": [
                    {
                        "OAuth2": []
                    },
                    {}
                ],
                "x-atlassian-connect-scope": "READ"
            }
        },
        "/rest/servicedeskapi/insight/workspace": {
            "get": {
                "tags": [
                    "Insight"
                ],
                "summary": "Get insight workspaces",
                "description": "Returns a list of Insight workspace IDs. Include a workspace ID in the path to access the [Insight REST APIs](https://developer.atlassian.com/cloud/insight/rest).\n\n**[Permissions](#permissions) required**: Any",
                "operationId": "getInsightWorkspaces",
                "parameters": [
                    {
                        "name": "start",
                        "in": "query",
                        "description": "The starting index of the returned workspace IDs. Base index: 0 See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32",
                            "default": 0
                        }
                    },
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "The maximum number of workspace IDs to return per page. Default: 50 See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32",
                            "default": 50
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returned if the request is successful.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/PagedDTOInsightWorkspaceDTO"
                                },
                                "example": "{\"_expands\":[],\"size\":1,\"start\":1,\"limit\":1,\"isLastPage\":true,\"_links\":{\"base\":\"https://your-domain.atlassian.net/rest/servicedeskapi\",\"context\":\"context\",\"next\":\"https://your-domain.atlassian.net/rest/servicedeskapi/rest/servicedeskapi/insight/workspace?start=2&limit=1\",\"prev\":\"https://your-domain.atlassian.net/rest/servicedeskapi/rest/servicedeskapi/insight/workspace?start=0&limit=1\"},\"values\":[{\"workspaceId\":\"1060ba0e-178b-4e0e-g0h1-jedb02cccb5f\"}]}"
                            }
                        }
                    },
                    "401": {
                        "description": "Returned if the authentication credentials are incorrect or missing.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "Returned if the user does not have the necessary permission.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "Internal Server Error.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    }
                },
                "deprecated": false,
                "security": [
                    {
                        "OAuth2": [
                            "read:servicedesk-request"
                        ]
                    }
                ],
                "x-atlassian-connect-scope": "INACCESSIBLE"
            }
        },
        "/rest/servicedeskapi/knowledgebase/article": {
            "get": {
                "tags": [
                    "Knowledgebase"
                ],
                "summary": "Get articles",
                "description": "Returns articles which match the given query string across all service desks.\n\n**[Permissions](#permissions) required**: Permission to access the [customer portal](https://confluence.atlassian.com/servicedeskcloud/configuring-the-customer-portal-732528918.html).",
                "operationId": "getArticles",
                "parameters": [
                    {
                        "name": "query",
                        "in": "query",
                        "description": "The string used to filter the articles (required).",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "highlight",
                        "in": "query",
                        "description": "If set to true matching query term in the title and excerpt will be highlighted using the \\{@code @@@hl@@@term@@@endhl@@@\\} syntax. Default: false.",
                        "schema": {
                            "type": "boolean",
                            "default": false
                        }
                    },
                    {
                        "name": "start",
                        "in": "query",
                        "description": "The starting index of the returned objects. Base index: 0. See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32"
                        }
                    },
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "The maximum number of items to return per page. Default: 100. See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns the articles, on the specified page of the results.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/PagedDTOArticleDTO"
                                },
                                "example": "{\"_expands\":[],\"size\":2,\"start\":2,\"limit\":2,\"isLastPage\":false,\"_links\":{\"base\":\"https://your-domain.atlassian.net/rest/servicedeskapi\",\"context\":\"context\",\"next\":\"https://your-domain.atlassian.net/rest/servicedeskapi/knowledgebase/article?start=4&limit=2\",\"prev\":\"https://your-domain.atlassian.net/rest/servicedeskapi/knowledgebase/article?start=0&limit=2\"},\"values\":[{\"title\":\"Stolen computer\",\"excerpt\":\"assuming your computer was stolen\",\"source\":{\"type\":\"confluence\",\"pageId\":\"8786177\",\"spaceKey\":\"IT\"},\"content\":{\"iframeSrc\":\"https://your-domain.atlassian.net/rest/servicedeskapi/knowledgebase/article/view/8786177\"}},{\"title\":\"Upgrading computer\",\"excerpt\":\"each computer older then 3 years can be upgraded\",\"source\":{\"type\":\"confluence\",\"pageId\":\"8785228\",\"spaceKey\":\"IT\"},\"content\":{\"iframeSrc\":\"https://your-domain.atlassian.net/rest/servicedeskapi/knowledgebase/article/view/8785228\"}}]}"
                            }
                        }
                    },
                    "400": {
                        "description": "Returned if the request is invalid. For example: missing query parameter.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "Returned if the user is not logged in.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "403": {
                        "description": "Returned if the user does not have permission to complete this request.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "Internal Server Error.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ErrorResponse"
                                }
                            }
                        }
                    }
                },
                "deprecated": false,
                "security": [
                    {
                        "OAuth2": [
                            "read:servicedesk-request"
                        ]
                    }
                ],
                "x-atlassian-oauth2-scopes": [
                    {
                        "state": "Current",
                        "scheme": "OAuth2",
                        "scopes": [
                            "read:servicedesk-request"
                        ]
                    },
                    {
                        "state": "Beta",
                        "scheme": "OAuth2",
                        "scopes": [
                            "read:knowledgebase:jira-service-management"
                        ]
                    }
                ],
                "x-experimental": true,
                "x-atlassian-connect-scope": "READ"
            }
        },
        "/rest/servicedeskapi/organization": {
            "get": {
                "tags": [
                    "Organization"
                ],
                "summary": "Get organizations",
                "description": "This method returns a list of organizations in the Jira Service Management instance. Use this method when you want to present a list of organizations or want to locate an organization by name.\n\n**[Permissions](#permissions) required**: Any. However, to fetch organizations based on `accountId` the user must have a Service Desk agent license.\n\n**Response limitations**: If the user is a customer, only those organizations of which the customer is a member are listed.",
                "operationId": "getOrganizations",
                "parameters": [
                    {
                        "name": "start",
                        "in": "query",
                        "description": "The starting index of the returned objects. Base index: 0. See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32"
                        }
                    },
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "The maximum number of organizations to return per page. Default: 50. See the [Pagination](#pagination) section for more details.",
                        "schema": {
                            "type": "integer",
                            "format": "int32"
                        }
                    },
                    {
                        "name": "accountId",
                        "in": "query",
                        "description": "The account ID of the user, which uniquely identifies the user across all Atlassian products. For example, *5b10ac8d82e05b22cc7d4ef5*.",
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns paginated list of organizations.",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/PagedDTOOrganizationDTO"
                                },
                                "example": "{\"_expands\":[],\"size\":1,\"start\":1,\"limit\":1,\"isLastPage\":false,\"_links\":{\"base\":\"https://your-domain.atlassian.net/rest/servicedeskapi\",\"context\":\"context\",\"next\":\"https://your-domain.atlassian.net/rest/servicedeskapi/organization?start=2&limit=1\",\"prev\":\"https://your-domain.atlassian.net/rest/servicedeskapi/organization?start=0&limit=1\"},\"values\":[{\"id\":\"1\",\"name\":\"Charlie Cakes Franchises\",\"_links\":{\"self\":\"https://your-domain.atlassian.net/rest/servicedeskapi/organization/1\"}}]}"
