                        "name": "purchaseOrderState",
                        "in": "query",
                        "description": "Filters purchase orders based on the purchase order state.",
                        "schema": {
                            "type": "string",
                            "enum": [
                                "New",
                                "Acknowledged",
                                "Closed"
                            ],
                            "x-docgen-enum-table-extension": [
